css:
@keyframes spin {
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}
.container-identity {
  width: 50%;
  min-width: 530px;
  margin: auto;
  padding-top: 10px;
}
.container-identity .id-header {
  color: rgba(35, 31, 32, 0.5);
  margin-bottom: 10px;
}
.container-identity .refresh {
  float: right;
  color: rgba(35, 31, 32, 0.5);
  margin-bottom: 10px;
}
.container-identity .refresh img {
  background-color: rgba(35, 31, 32, 0.5);
}
.container-identity .refresh.spinning img {
  animation: spin 1.4s linear infinite;
}
.container-identity .identity-content-box {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  color: rgba(35, 31, 32, 0.8);
  border-radius: 5px;
  border: 1px solid #e6e6e6;
  background-color: #f6f6f6;
}
.container-identity .identity-content-box .row {
  display: flex;
  align-items: center;
  width: 100%;
}
.container-identity .identity-content-box .payment-row {
  flex-direction: column;
  align-items: flex-start;
  border-top: 1px solid #e6e6e6;
  padding: 20px;
  padding-left: 137px;
}
.container-identity .identity-content-box .payment-row > div:first-child {
  margin-bottom: 10px;
}
.container-identity .identity-content-box .info-row {
  padding: 30px;
}
.container-identity .identity-content-box .info-row .logo {
  margin-right: 30px;
}
.container-identity .identity-content-box .info-row .identity-info {
  line-height: 1.9em;
}
.container-identity .identity-content-box .info-row .identity-info .name {
  font-size: 1.2em;
}
.container-identity .identity-content-box .info-row .identity-info .identity-actions {
  margin-top: 5px;
}
.container-identity .identity-content-box .info-row .identity-info .identity-actions .btn {
  width: 150px;
  text-align: center;
}
.container-identity .identity-content-box .info-row .identity-info .identity-actions .btn:first-child {
  margin-right: 12px;
}
