css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.calendar-event .rm-time {
  font-size: 13px;
  padding: 3px 5px 10px 10px;
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
  text-align: right;
  cursor: default;
  color: rgba(35, 31, 32, 0.5);
}
.calendar-event .rm-time:hover {
  color: rgba(35, 31, 32, 0.8);
}
.calendar-event .event-injected-components {
  position: absolute;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
}
.scheduler-picker {
  min-width: 186px;
}
img[data-overlay-id].new-event-card-container {
  display: block;
}
.new-event-card-container .new-event-card {
  border-radius: 4px;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08);
  padding: 10px 0;
  padding-right: 30px;
}
.new-event-card-container .new-event-card .remove-button {
  position: absolute;
  right: 5px;
  top: 0;
  color: #CCC;
  padding: 10px;
  cursor: default;
}
.new-event-card-container .new-event-card .remove-button:hover {
  color: #231f20;
}
.new-event-card-container .new-event-card .row {
  position: relative;
  display: flex;
  align-items: baseline;
  padding: 10px 0;
}
.new-event-card-container .new-event-card .row.title {
  font-size: 20.02px;
}
.new-event-card-container .new-event-card .suggest-times {
  padding-left: 56px;
  color: #A9A9A9;
  padding-bottom: 10px;
}
.new-event-card-container .new-event-card .suggest-times a {
  margin-left: 0.5em;
}
.new-event-card-container .new-event-card .suggest-times a:hover {
  cursor: default;
}
.new-event-card-container .new-event-card .row.expand,
.new-event-card-container .new-event-card .row.time {
  color: #A9A9A9;
}
.new-event-card-container .new-event-card .row.expand .timezone,
.new-event-card-container .new-event-card .row.time .timezone {
  font-size: 10px;
  font-weight: 600;
}
.new-event-card-container .new-event-card .row.time .time-picker {
  text-align: center;
}
.new-event-card-container .new-event-card .row.time .time-picker-wrap {
  margin-right: 5px;
}
.new-event-card-container .new-event-card .row.time .time-picker-wrap .time-options {
  z-index: 10;
}
.new-event-card-container .new-event-card .datetime-input-container {
  padding: 0 5px;
  display: inline-block;
}
.new-event-card-container .new-event-card .datetime-input-container input {
  margin: 0;
}
.new-event-card-container .new-event-card .datetime-input-container:first-child {
  padding-left: 0;
}
.new-event-card-container .new-event-card .datetime-input-container:first-child input[type=date] {
  padding-left: 0;
}
.new-event-card-container .new-event-card .row textarea {
  border-bottom: 1px solid #EEE;
}
.new-event-card-container .new-event-card .row textarea:focus {
  box-shadow: 0 0 0;
}
.new-event-card-container .new-event-card input[type=text] {
  border-radius: 0;
  display: inline-block;
  flex: 1;
  border-bottom: 1px solid #EEE;
}
.new-event-card-container .new-event-card input[type=text]:focus {
  border: none;
  box-shadow: none;
  border-bottom: 1px solid #EEE;
}
.new-event-card-container .new-event-card .field-icon {
  padding: 0 20px;
}
.new-event-card-container .new-event-card .field-icon img.content-mask {
  background: #CCC;
}
.new-event-card-container .new-event-card .proposals-wrap {
  margin-top: 10px;
  display: flex;
  padding-left: 58px;
}
.new-event-card-container .new-event-card .proposal-day {
  flex: 1;
  border: 1px solid #dddddd;
  border-radius: 3px;
  margin-right: 15px;
  text-align: center;
}
.new-event-card-container .new-event-card .proposal-day:last-child {
  margin-right: 0;
}
.new-event-card-container .new-event-card .day-header {
  padding: 10px;
  border-bottom: 1px solid #dddddd;
}
.new-event-card-container .new-event-card .proposal {
  padding: 10px;
  border-bottom: 1px solid #dddddd;
}
.new-event-card-container .new-event-card .proposal:last-child {
  border-bottom: 0;
}
.new-event-card-container .new-event-card .day-header {
  text-transform: uppercase;
  color: rgba(35, 31, 32, 0.5);
}
