css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
*:focus,
input:focus {
  outline: none;
}
.thread-list .list-container,
.draft-list .list-container,
.thread-list .scroll-region,
.draft-list .scroll-region {
  width: 100%;
  height: 100%;
  -webkit-font-smoothing: subpixel-antialiased;
}
.thread-list .swipe-backing,
.draft-list .swipe-backing {
  background-color: #e6e6e6;
}
.thread-list .swipe-backing::after,
.draft-list .swipe-backing::after {
  color: rgba(255, 255, 255, 0.9);
  padding-top: 45px;
  text-align: center;
  font-weight: 400;
  font-size: 13.02px;
  position: absolute;
  top: 0;
  transform: translateX(0%);
  width: 80px;
  bottom: 0;
  opacity: 0.8;
  transition: opacity linear 150ms;
  background-repeat: no-repeat;
  background-position: 50% 35%;
  background-size: 24px 24px;
}
.thread-list .swipe-backing.swipe-trash,
.draft-list .swipe-backing.swipe-trash {
  transition: background-color linear 150ms;
  background-color: #f26478;
}
.thread-list .swipe-backing.swipe-trash::after,
.draft-list .swipe-backing.swipe-trash::after {
  transition: left linear 150ms, transform linear 150ms;
  content: "Trash";
  left: 0;
  background-image: url(../static/images/swipe/icon-swipe-trash@2x.png);
}
.thread-list .swipe-backing.swipe-trash.confirmed,
.draft-list .swipe-backing.swipe-trash.confirmed {
  background-color: #ed304b;
}
.thread-list .swipe-backing.swipe-trash.confirmed::after,
.draft-list .swipe-backing.swipe-trash.confirmed::after {
  left: 100%;
  transform: translateX(-100%);
  opacity: 1;
}
.thread-list .swipe-backing.swipe-archive,
.draft-list .swipe-backing.swipe-archive,
.thread-list .swipe-backing.swipe-all,
.draft-list .swipe-backing.swipe-all {
  transition: background-color linear 150ms;
  background-color: #91df58;
}
.thread-list .swipe-backing.swipe-archive::after,
.draft-list .swipe-backing.swipe-archive::after,
.thread-list .swipe-backing.swipe-all::after,
.draft-list .swipe-backing.swipe-all::after {
  transition: left linear 150ms, transform linear 150ms;
  content: "Archive";
  left: 0;
  background-image: url(../static/images/swipe/icon-swipe-archive@2x.png);
}
.thread-list .swipe-backing.swipe-archive.confirmed,
.draft-list .swipe-backing.swipe-archive.confirmed,
.thread-list .swipe-backing.swipe-all.confirmed,
.draft-list .swipe-backing.swipe-all.confirmed {
  background-color: #6cd420;
}
.thread-list .swipe-backing.swipe-archive.confirmed::after,
.draft-list .swipe-backing.swipe-archive.confirmed::after,
.thread-list .swipe-backing.swipe-all.confirmed::after,
.draft-list .swipe-backing.swipe-all.confirmed::after {
  left: 100%;
  transform: translateX(-100%);
  opacity: 1;
}
.thread-list .swipe-backing.swipe-snooze,
.draft-list .swipe-backing.swipe-snooze {
  transition: background-color linear 150ms;
  background-color: #aa90ea;
}
.thread-list .swipe-backing.swipe-snooze::after,
.draft-list .swipe-backing.swipe-snooze::after {
  transition: right linear 150ms, transform linear 150ms;
  content: "Snooze";
  right: 0;
  background-image: url(../static/images/swipe/icon-swipe-snooze@2x.png);
}
.thread-list .swipe-backing.swipe-snooze.confirmed,
.draft-list .swipe-backing.swipe-snooze.confirmed {
  background-color: #8d6be3;
}
.thread-list .swipe-backing.swipe-snooze.confirmed::after,
.draft-list .swipe-backing.swipe-snooze.confirmed::after {
  right: 100%;
  transform: translateX(100%);
  opacity: 1;
}
.thread-list .list-item,
.draft-list .list-item {
  background-color: #fafafa;
  border-bottom: 1px solid rgba(221, 221, 221, 0.6);
  line-height: 36px;
}
.thread-list .mail-important-icon,
.draft-list .mail-important-icon {
  margin-left: 6px;
  padding: 12px;
  vertical-align: initial;
}
.thread-list .mail-important-icon:not(.active),
.draft-list .mail-important-icon:not(.active) {
  visibility: hidden;
}
.thread-list .message-count,
.draft-list .message-count {
  color: #ffffff;
  background: #6d7987;
  padding: 4px 6px 2px 6px;
  margin-left: 1em;
}
.thread-list .draft-icon,
.draft-list .draft-icon {
  margin-left: 10px;
  flex-shrink: 0;
  object-fit: contain;
}
.thread-list .participants,
.draft-list .participants {
  font-size: 13.02px;
  text-overflow: ellipsis;
  text-align: left;
  overflow: hidden;
}
.thread-list .participants.no-recipients,
.draft-list .participants.no-recipients {
  color: rgba(35, 31, 32, 0.5);
}
.thread-list .details,
.draft-list .details {
  display: flex;
  align-items: center;
  overflow: hidden;
}
.thread-list .details .subject,
.draft-list .details .subject {
  font-size: 13.02px;
  font-weight: 400;
  padding-right: 12px;
  text-overflow: ellipsis;
  overflow: hidden;
  flex-shrink: 0.1;
}
.thread-list .details .snippet,
.draft-list .details .snippet {
  font-size: 13.02px;
  font-weight: 400;
  text-overflow: ellipsis;
  overflow: hidden;
  opacity: 0.62;
  flex: 1;
}
.thread-list .details .thread-icon,
.draft-list .details .thread-icon {
  margin-right: 12px;
  margin-left: 12px;
}
.thread-list .list-column-State,
.draft-list .list-column-State {
  display: flex;
  align-items: center;
}
.thread-list .list-column-Date,
.draft-list .list-column-Date {
  text-align: right;
}
.thread-list .timestamp,
.draft-list .timestamp {
  font-size: 13.02px;
  font-weight: 400;
  min-width: 70px;
  margin-right: 8px;
  opacity: 0.62;
}
.thread-list .unread:not(.focused):not(.selected),
.draft-list .unread:not(.focused):not(.selected) {
  background-color: #ffffff;
}
.thread-list .unread:not(.focused):not(.selected):hover,
.draft-list .unread:not(.focused):not(.selected):hover {
  background: #fafafa;
}
.thread-list .unread:not(.focused):not(.selected) .snippet,
.draft-list .unread:not(.focused):not(.selected) .snippet {
  color: rgba(35, 31, 32, 0.8);
}
.thread-list .unread:not(.focused):not(.selected):not(.next-is-selected),
.draft-list .unread:not(.focused):not(.selected):not(.next-is-selected) {
  border-bottom: 1px solid #dddddd;
}
.thread-list .unread:not(.focused) .subject,
.draft-list .unread:not(.focused) .subject {
  font-weight: 600;
}
.thread-list .unread:not(.focused) .subject .emoji,
.draft-list .unread:not(.focused) .subject .emoji {
  font-weight: 400;
}
.thread-list .unread:not(.focused) .participants .unread-true,
.draft-list .unread:not(.focused) .participants .unread-true {
  font-weight: 600;
}
.thread-list .focused,
.draft-list .focused {
  -webkit-font-smoothing: antialiased;
  color: #ffffff;
}
.thread-list .focused .participants .unread-true,
.draft-list .focused .participants .unread-true {
  font-weight: 400;
}
.thread-list .focused .subject,
.draft-list .focused .subject {
  font-weight: 400;
}
.thread-list .focused .thread-icon,
.draft-list .focused .thread-icon,
.thread-list .focused .draft-icon,
.draft-list .focused .draft-icon,
.thread-list .focused .mail-important-icon,
.draft-list .focused .mail-important-icon {
  -webkit-filter: brightness(600%) grayscale(100%);
}
.thread-list .focused .mail-label,
.draft-list .focused .mail-label {
  background: rgba(255, 255, 255, 0.2) !important;
  box-shadow: none !important;
  -webkit-filter: brightness(600%) grayscale(100%);
}
.thread-list .thread-injected-icons,
.draft-list .thread-injected-icons {
  vertical-align: top;
}
.thread-list .thread-injected-mail-labels,
.draft-list .thread-injected-mail-labels {
  margin-right: 6px;
}
.thread-list .thread-icon,
.draft-list .thread-icon {
  width: 25px;
  height: 24px;
  flex-shrink: 0;
  background-size: 15px;
  display: inline-block;
  background-repeat: no-repeat;
  background-position: center;
}
.thread-list .thread-icon.thread-icon-attachment,
.draft-list .thread-icon.thread-icon-attachment {
  background-image: url(../static/images/thread-list/icon-attachment-@2x.png);
  margin-right: 0;
  margin-left: 0;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  background-image: url(../static/images/thread-list/icon-unread-@2x.png);
}
.thread-list .thread-icon.thread-icon-replied,
.draft-list .thread-icon.thread-icon-replied {
  background-image: url(../static/images/thread-list/icon-replied-@2x.png);
}
.thread-list .thread-icon.thread-icon-forwarded,
.draft-list .thread-icon.thread-icon-forwarded {
  background-image: url(../static/images/thread-list/icon-forwarded-@2x.png);
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  background-size: 16px;
  background-image: url(../static/images/thread-list/icon-star-@2x.png);
}
.thread-list .star-button,
.draft-list .star-button {
  font-size: 16px;
}
.thread-list .star-button .fa-star,
.draft-list .star-button .fa-star {
  color: #efd100;
}
.thread-list .star-button .fa-star:hover,
.draft-list .star-button .fa-star:hover {
  cursor: pointer;
  color: #dcdcdc;
}
.thread-list .star-button .fa-star-o,
.draft-list .star-button .fa-star-o {
  color: #dcdcdc;
}
.thread-list .star-button .fa-star-o:hover,
.draft-list .star-button .fa-star-o:hover {
  cursor: pointer;
  color: #efd100;
}
.thread-list .list-item .list-column-HoverActions {
  display: none;
}
.thread-list .list-item .list-column-HoverActions .action {
  display: inline-block;
  background-size: 100%;
  zoom: 0.5;
  width: 81px;
  height: 51px;
  margin: 9px 16px 0 16px;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url("../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png") center no-repeat, linear-gradient(to top, rgba(241, 241, 241, 0.75) 0%, rgba(253, 253, 253, 0.75) 100%);
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url("../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png") center no-repeat, linear-gradient(to top, rgba(241, 241, 241, 0.75) 0%, rgba(253, 253, 253, 0.75) 100%);
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url("../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png") center no-repeat, linear-gradient(to top, rgba(241, 241, 241, 0.75) 0%, rgba(253, 253, 253, 0.75) 100%);
}
body.platform-win32 .thread-list .list-item .list-column-HoverActions .action {
  border: 0;
  margin: 9px 0 0 0;
}
.thread-list .list-item:hover .list-column-HoverActions {
  width: 0;
  padding: 0;
  display: block;
  overflow: visible;
  height: 100%;
}
.thread-list .list-item:hover .list-column-HoverActions .inner {
  position: relative;
  width: 300px;
  height: 100%;
  left: -300px;
}
.thread-list .list-item:hover .list-column-HoverActions .inner .thread-injected-quick-actions {
  margin-right: 10px;
}
.thread-list .list-item:hover .list-column-HoverActions .inner {
  background-image: -webkit-linear-gradient(left, rgba(242, 242, 242, 0) 0%, #f2f2f2 50%, #f2f2f2 100%);
}
.thread-list .list-item.selected:hover .list-column-HoverActions .inner {
  background-image: -webkit-linear-gradient(left, rgba(220, 235, 250, 0) 0%, #dcebfa 50%, #dcebfa 100%);
}
.thread-list .list-item.focused:hover .list-column-HoverActions .inner {
  background-image: -webkit-linear-gradient(left, rgba(49, 135, 225, 0) 0%, #3187e1 50%, #3187e1 100%);
}
.thread-list .list-item.focused:hover .list-column-HoverActions .inner .action {
  -webkit-filter: invert(100%) brightness(300%);
}
.thread-list .list-item.focused:hover .list-column-HoverActions .inner .action.action-archive {
  background: url("../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png") center no-repeat;
}
.thread-list .list-item.focused:hover .list-column-HoverActions .inner .action.action-trash {
  background: url("../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png") center no-repeat;
}
.thread-list .list-item.focused:hover .list-column-HoverActions .inner .action.action-snooze {
  background: url("../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png") center no-repeat;
}
.thread-list .thread-icon-star:hover {
  background-image: url(../static/images/thread-list/icon-star-@2x.png);
  background-size: 16px;
  -webkit-filter: brightness(90%);
}
.thread-list .list-item:hover .thread-icon-none:hover {
  background-image: url(../static/images/thread-list/icon-star-action-hover-@2x.png);
  background-size: 16px;
}
.thread-list .list-item:hover .thread-icon-none {
  background-image: url(../static/images/thread-list/icon-star-hover-@2x.png);
  background-size: 16px;
}
.thread-list .list-item:hover .mail-important-icon.enabled {
  visibility: inherit;
}
.thread-list .thread-icon-star-on-hover:hover {
  background-image: url(../static/images/thread-list/icon-star-hover-@2x.png);
  background-size: 16px;
}
.thread-list-narrow .icons-column {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 25px;
  margin-right: 5px;
}
.thread-list-narrow .icons-column .thread-injected-icons {
  align-items: center;
}
.thread-list-narrow .thread-info-column {
  flex: 1;
  align-self: center;
  overflow: hidden;
}
.thread-list-narrow .thread-info-column .participants-wrapper {
  display: flex;
  align-items: center;
  min-height: 24px;
}
.thread-list-narrow .list-column {
  display: block;
}
.thread-list-narrow .list-tabular-item {
  line-height: 21px;
}
.thread-list-narrow .timestamp {
  order: 100;
  min-width: 0;
}
.thread-list-narrow .participants {
  font-size: 14px;
}
.thread-list-narrow .mail-important-icon {
  margin-left: 1px;
  float: left;
  padding: 12px;
  vertical-align: initial;
}
.thread-list-narrow .subject {
  font-size: 14px;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: left;
  margin-right: 8px;
}
.thread-list-narrow .snippet-and-labels {
  margin-right: 8px;
  display: flex;
  align-items: baseline;
  overflow: hidden;
}
.thread-list-narrow .snippet-and-labels .mail-label {
  font-size: 0.8em;
  line-height: 17px;
}
.thread-list-narrow .snippet-and-labels .snippet {
  font-size: 13.02px;
  overflow: hidden;
  text-overflow: ellipsis;
  opacity: 0.7;
  text-align: left;
  min-height: 21px;
  margin-right: 4px;
}
.thread-list.handler-split .list-item.selected {
  background: #3187e1;
  -webkit-font-smoothing: antialiased;
  color: #ffffff;
}
.thread-list.handler-split .list-item.selected .participants .unread-true {
  font-weight: 400;
}
.thread-list.handler-split .list-item.selected .subject {
  font-weight: 400;
}
.thread-list.handler-split .list-item.selected .thread-icon,
.thread-list.handler-split .list-item.selected .draft-icon,
.thread-list.handler-split .list-item.selected .mail-important-icon {
  -webkit-filter: brightness(600%) grayscale(100%);
}
.thread-list.handler-split .list-item.selected .mail-label {
  background: rgba(255, 255, 255, 0.2) !important;
  box-shadow: none !important;
  -webkit-filter: brightness(600%) grayscale(100%);
}
.thread-list.handler-split .list-item.selected:hover .list-column-HoverActions .inner {
  background-image: -webkit-linear-gradient(left, rgba(49, 135, 225, 0) 0%, #3187e1 50%, #3187e1 100%);
}
.thread-list.handler-split .list-item.selected:hover .list-column-HoverActions .inner .action {
  -webkit-filter: invert(100%) brightness(300%);
}
.thread-list.handler-split .list-item.selected:hover .list-column-HoverActions .inner .action.action-archive {
  background: url("../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png") center no-repeat;
}
.thread-list.handler-split .list-item.selected:hover .list-column-HoverActions .inner .action.action-trash {
  background: url("../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png") center no-repeat;
}
.thread-list.handler-split .list-item.selected:hover .list-column-HoverActions .inner .action.action-snooze {
  background: url("../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png") center no-repeat;
}
body.is-blurred .thread-list.handler-split .list-item.selected {
  background: rgba(137, 137, 137, 0.35);
  border-bottom: 1px solid rgba(137, 137, 137, 0.35);
  color: #231f20;
}
