css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.link-tracking-icon img.content-mask {
  background-color: #AAA;
  vertical-align: text-bottom;
}
.link-tracking-icon img.content-mask.clicked {
  background-color: #CCC;
}
.link-tracking-icon .link-click-count {
  display: inline-block;
  position: relative;
  left: -16px;
  text-align: center;
  color: #3187e1;
  font-size: 12px;
  font-weight: bold;
}
.link-tracking-icon {
  width: 16px;
  margin-right: 4px;
}
.link-tracking-panel {
  background: #DDF6FF;
  border: 1px solid #ACD;
  padding: 5px;
  border-radius: 5px;
}
.link-tracking-panel h4 {
  text-align: center;
  margin-top: 0;
}
.link-tracking-panel table {
  width: 100%;
}
.link-tracking-panel td {
  border-bottom: 1px solid #D5EAF5;
  border-top: 1px solid #D5EAF5;
  padding: 0 10px;
  text-align: left;
}
.link-tracking-message-popover {
  width: 200px;
  max-height: 134px;
}
.link-tracking-message-popover .link-tracking-header {
  padding: 5px 12px 0 12px;
  text-align: center;
  color: rgba(35, 31, 32, 0.8);
  font-weight: 600;
}
.link-tracking-message-popover .click-history-container {
  max-height: 112px;
  padding: 0 12px 5px 12px;
  overflow: auto;
}
.link-tracking-message-popover .click-history-container .click-action {
  color: rgba(35, 31, 32, 0.8);
}
.link-tracking-message-popover .click-history-container .click-action .recipient {
  text-overflow: ellipsis;
  overflow: hidden;
}
.link-tracking-message-popover .click-history-container .click-action .spacer {
  flex: 1 1 0;
}
.link-tracking-message-popover .click-history-container .click-action .timestamp {
  color: rgba(35, 31, 32, 0.5);
  flex-shrink: 0;
}
