css:
img.activity-toolbar-icon.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/wave-24.svg') center center / 32px no-repeat !important;
}
.btn.btn-toolbar.item-compose img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/write-24.svg') center center / 32px no-repeat !important;
}
.btn-toolbar[title~="Trash"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/trash-24.svg') center center / 32px no-repeat !important;
}
.btn-toolbar[title~="Archive"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/archive-24.svg') center center / 32px no-repeat !important;
}
.btn-toolbar[title~="Spam"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/spam-24.svg') center center / 29px no-repeat !important;
}
.btn-toolbar[title~="unread"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/unread-24.svg') center center / 29px no-repeat !important;
}
.btn-toolbar[title~="read"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/read-24.svg') center center / 29px no-repeat !important;
}
.btn-toolbar[title~="Snooze"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/snooze-24.svg') center center / 29px no-repeat !important;
}
.btn-toolbar[title~="Labels"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/label-24.svg') center center / 29px no-repeat !important;
}
.btn-toolbar[title~="Star"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/star-24.svg') center center / 29px no-repeat !important;
}
.btn-toolbar[title~="sidebar"] img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/user-24.svg') center center / 29px no-repeat !important;
}
.btn-icon.message-toolbar-arrow.down img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/arrow-down-24.svg') center center / 29px no-repeat !important;
}
.btn-icon.message-toolbar-arrow.up img.content-mask {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/arrow-up-24.svg') center center / 29px no-repeat !important;
}
.btn.action.action-snooze {
  background: url('nylas://Bemind-N1-Theme/icons/snooze-24.svg') center center / 24px no-repeat, linear-gradient(to top, rgba(241, 241, 241, 0.75) 0%, rgba(253, 253, 253, 0.75) 100%) !important;
}
.btn.action.action-archive {
  background: url('nylas://Bemind-N1-Theme/icons/archive-24.svg') center center / 24px no-repeat, linear-gradient(to top, rgba(241, 241, 241, 0.75) 0%, rgba(253, 253, 253, 0.75) 100%) !important;
}
.btn.action.action-trash {
  background: url('nylas://Bemind-N1-Theme/icons/trash-24.svg') center center / 24px no-repeat, linear-gradient(to top, rgba(241, 241, 241, 0.75) 0%, rgba(253, 253, 253, 0.75) 100%) !important;
}
.message-icons-wrap .content-mask[title="Print Thread"] {
  -webkit-mask: url('nylas://Bemind-N1-Theme/icons/print-24.svg') center center / 24px no-repeat !important;
}
#message-list {
  background-color: #ffffff;
}
#message-list .messages-wrap .scroll-region-content-inner {
  padding: 32px;
}
.preferences-wrap .container-preference-tabs .preferences-tabs {
  padding-bottom: 10px;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item {
  padding: 10px 0 0;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item.active {
  background: transparent;
  border-radius: 4px;
}
.tab-icon {
  transition: all 200ms cubic-bezier(0.645, 0.045, 0.355, 1);
}
.preferences-tabs .item:not(.active):hover .tab-icon {
  transform: translateY(-10px);
}
@font-face {
  font-family: 'Montserrat';
  src: url('nylas://Bemind-N1-Theme/styles/fonts/montserrat-black-webfont.woff2') format('woff2');
  font-weight: normal;
  font-style: normal;
}
@font-face {
  font-family: 'Montserrat';
  src: url('nylas://Bemind-N1-Theme/styles/fonts/montserrat-bold-webfont.woff2') format('woff2');
  font-weight: 700;
  font-style: normal;
}
@font-face {
  font-family: 'Montserrat';
  src: url('nylas://Bemind-N1-Theme/styles/fonts/montserrat-hairline-webfont.woff2') format('woff2');
  font-weight: 100;
  font-style: normal;
}
@font-face {
  font-family: 'Montserrat';
  src: url('nylas://Bemind-N1-Theme/styles/fonts/montserrat-light-webfont.woff2') format('woff2');
  font-weight: 300;
  font-style: normal;
}
@font-face {
  font-family: 'Montserrat';
  src: url('nylas://Bemind-N1-Theme/styles/fonts/montserrat-regular-webfont.woff2') format('woff2');
  font-weight: 400;
  font-style: normal;
}
.btn {
  border-radius: 2px;
  box-shadow: none !Important;
}
.platform-darwin .toolbar-window-controls {
  margin-left: 20px;
}
.platform-darwin .sheet-toolbar {
  height: 58px;
  min-height: 58px;
  max-height: 58px;
}
.sheet-toolbar .toolbar-RootSidebar,
.sheet-toolbar .toolbar-MessageList,
.sheet-toolbar .toolbar-ThreadList,
.sheet-toolbar .item-container {
  top: 10px;
}
.sheet-toolbar .selection-bar .absolute {
  border-left: 1px solid #e6e6e6;
}
.sheet-toolbar .btn-toolbar {
  background: #ffffff;
  box-shadow: none;
}
.btn.btn-toolbar img.content-mask {
  transition: background-color 100ms;
}
.btn.btn-toolbar:hover img.content-mask {
  background-color: #ffc41e;
}
.platform-darwin .search-bar .search-accessory.search {
  left: 16px;
  top: 20px;
}
.platform-darwin .search-bar {
  width: 440px;
  margin-top: 0;
}
.search-bar .menu .header-container input {
  box-shadow: none;
  border-bottom: 1px solid #dddddd;
  border-radius: 0;
  padding: 16px 32px 16px 32px;
}
.nylas-outline-view .item .item-count-box {
  box-shadow: none;
  background-color: #ffd151;
  color: #D6AB15;
  padding: 0 0.386em 0 0.386em;
}
.account-sidebar-sections {
  padding: 0 16px 16px 8px;
}
.flexbox-handle-horizontal div {
  box-shadow: 0.5px 0 0 #e6e6e6;
}
.account-switcher {
  top: 13px;
  right: 24px;
  transform: scale(1.3);
}
.nylas-outline-view .heading {
  margin-bottom: 16px;
  font-size: 10px;
  line-height: 1.6;
  letter-spacing: 1.4px;
}
.nylas-outline-view .item-container {
  padding: 0 0 8px 0;
}
.nylas-outline-view .item div,
.nylas-outline-view .item div img {
  transition: all 100ms;
}
.nylas-outline-view .item-container .disclosure-triangle div {
  border-left: 7px solid rgba(35, 31, 32, 0.18);
}
.scrollbar-track {
  background: transparent;
  border-left: none;
  width: 11px;
}
.scrollbar-track .scrollbar-handle {
  border: none;
  background-color: rgba(0, 0, 0, 0.14);
}
.thread-list.handler-split .list-item.selected {
  color: #5b400c !important;
}
.thread-list.handler-split .list-item.selected .subject {
  color: #231f20;
}
.thread-list .focused .thread-icon,
.draft-list .focused .thread-icon,
.thread-list .focused .draft-icon,
.draft-list .focused .draft-icon {
  -webkit-filter: brightness(600%) grayscale(100%) invert(80%);
}
.thread-list .focused .mail-important-icon,
.draft-list .focused .mail-important-icon {
  -webkit-filter: brightness(600%) grayscale(100%) invert(80%);
  opacity: 0.5;
}
.list-tabular .list-tabular-item.selected .checkmark .inner {
  background: #ffecb7;
  background-image: url('nylas://Bemind-N1-Theme/icons/check-16.svg');
  background-size: 10px;
  background-repeat: no-repeat;
  background-position: center;
}
.notifications-sticky .notifications-sticky-item {
  border-bottom: none;
}
