css:
.sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #41403b, #34332f);
  box-shadow: none;
}
.sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(255, 255, 255, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(255, 255, 255, 0.8);
}
.account-sidebar {
  flex: 1;
  height: 100%;
  background-color: #f6f7f8;
}
.account-sidebar .item.deleted {
  opacity: 0.5;
}
.account-sidebar .nylas-outline-view:first-child .heading span {
  margin-right: 30px;
}
.account-switcher {
  position: absolute;
  top: 7px;
  right: 17px;
  z-index: 3;
}
.account-switcher img {
  transform: rotateX(180deg);
}
