css:
.sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #41403b, #34332f);
  box-shadow: none;
}
.sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(255, 255, 255, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(255, 255, 255, 0.8);
}
.template-picker .content-container {
  height: 150px;
  width: 210px;
  overflow-y: scroll;
}
.template-picker .footer-container {
  border-top: 1px solid #ffffff;
}
.template-status-bar {
  background-color: #fcf4db;
  color: #695209;
  border: 1.5px solid #f8e6ac;
  border-radius: 2px;
  padding-top: 4px 10px 4px 10px;
  font-size: 13.02px;
  margin-bottom: 10px;
}
.compose-body code.var,
.container-templates .contenteditable code.var {
  font: inherit;
  padding: 0;
  padding-left: 2px;
  padding-right: 2px;
  border-bottom: 1.5px solid #f8e6ac;
  background-color: #fcf4db;
}
.compose-body code.var.empty,
.container-templates .contenteditable code.var.empty {
  color: #c79b11;
}
.container-templates {
  max-width: 640px;
  margin: 0 auto;
}
.container-templates .no-templates-message {
  text-align: center;
  margin-top: 50px;
}
.container-templates .template-wrap {
  position: relative;
  border: 1px solid rgba(230, 230, 230, 0.9);
  background-color: #ffffff;
  padding: 10px;
  margin-top: 20px;
  min-height: 200px;
  display: flex;
}
.container-templates .template-wrap .contenteditable-container {
  min-height: 200px;
}
.container-templates .template-wrap textarea {
  border: 0;
  padding: 0;
  flex: 1;
  font-family: monospace;
  font-size: 0.9em;
}
.container-templates .section-title {
  display: inline-flex;
  width: 100%;
  flex-wrap: wrap;
  align-items: center;
}
.container-templates .section-title span {
  line-height: 1.7;
}
.container-templates .section-title select {
  flex-basis: 100px;
  flex-grow: 1;
  margin: 0 0 0 8px;
}
.container-templates .section-title input {
  flex-grow: 1;
  margin: 0 0 0 8px;
}
.container-templates .section-body {
  padding: 10px 0 0 0;
}
.container-templates .section-body .menu {
  border: solid thin #CCC;
  margin-right: 5px;
  min-height: 200px;
}
.container-templates .section-body .menu .menu-items {
  margin: 0;
  padding: 0;
  list-style: none;
}
.container-templates .section-body .menu .menu-items li {
  padding: 6px;
}
.container-templates .section-body .menu-horizontal {
  height: 100%;
}
.container-templates .section-body .menu-horizontal .menu-items {
  height: 100%;
  margin: 0;
  padding: 0;
  list-style: none;
}
.container-templates .section-body .menu-horizontal .menu-items li {
  text-align: center;
  width: 40px;
  display: inline-block;
  padding: 8px 16px 8px 16px;
  border-right: solid thin #CCC;
}
.container-templates .section-body .template-area {
  border: solid thin #CCC;
  min-height: 200px;
}
.container-templates .section-body .menu-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.container-templates .section-body .template-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.container-templates .section-body .template-footer .edit-html-button {
  float: right;
  margin: 6px;
}
.container-templates .template-name-btn {
  float: right;
  margin: 0 6px;
}
.container-templates .template-name-input {
  display: inline-block;
  width: 100px;
}
.container-templates .editor-note {
  color: #AAA;
  font-size: small;
  margin-top: 5px;
}
.container-templates .templates-instructions {
  color: #333;
  font-size: small;
  margin-top: 20px;
}
