css:
.sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #41403b, #34332f);
  box-shadow: none;
}
.sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
.container-mail-rules {
  max-width: 800px;
  margin: 0 auto;
}
.container-mail-rules .empty-list {
  height: 376px;
  width: inherit;
  background-color: #f6f6f6;
  border: 1px solid #ffffff;
  text-align: center;
}
.container-mail-rules .empty-list .icon-mail-rules {
  margin-top: 80px;
}
.container-mail-rules .empty-list h2 {
  color: rgba(35, 31, 32, 0.5);
}
.container-mail-rules .empty-list .btn {
  margin-top: 10px;
}
.container-mail-rules .rule-list {
  position: relative;
  height: inherit;
  width: inherit;
}
.container-mail-rules .rule-list .items-wrapper {
  min-width: 200px;
  height: 350px;
}
.container-mail-rules .rule-list .item-rule-disabled {
  color: #d9534f;
  padding: 4px 10px;
  border-bottom: 1px solid #ffffff;
}
.container-mail-rules .rule-list .selected .item-rule-disabled {
  color: #ffffff;
}
.container-mail-rules .rule-detail {
  flex: 1;
  cursor: default;
  background-color: #f6f6f6;
  border: 1px solid #ffffff;
  border-left: 0;
}
.container-mail-rules .rule-detail .disabled-reason {
  padding: 10px 10px;
  background-color: rgba(202, 37, 65, 0.3);
  border-bottom: 1px solid #ca2541;
  margin-bottom: 5px;
}
.container-mail-rules .rule-detail .disabled-reason .btn {
  margin-left: 24px;
  float: right;
}
.container-mail-rules .rule-detail .inner {
  padding: 5px 12px;
}
.container-mail-rules .rule-detail .no-selection {
  color: rgba(35, 31, 32, 0.5);
  text-align: center;
  padding: 100px;
}
.container-mail-rules .rule-detail .well {
  background-color: #ffffff;
  border: 1px solid #ffffff;
  margin: 5px 0;
  font-size: 0.9em;
}
.container-mail-rules .rule-detail .well .well-row {
  padding: 5px 12px;
  border-bottom: 1px solid #ffffff;
}
.container-mail-rules .rule-detail .well .well-row select,
.container-mail-rules .rule-detail .well .well-row input {
  margin: 1.25px 6px;
}
.container-mail-rules .rule-detail .well .well-row select:first-child,
.container-mail-rules .rule-detail .well .well-row input:first-child {
  margin-left: 0;
}
.container-mail-rules .rule-detail .well .well-row select:last-child,
.container-mail-rules .rule-detail .well .well-row input:last-child {
  margin-right: 0;
}
.container-mail-rules .rule-detail .well .well-row select {
  max-width: 170px;
}
.container-mail-rules .rule-detail .well .well-row input {
  width: 200px;
}
.container-mail-rules .rule-detail .well .well-row .actions {
  white-space: nowrap;
  vertical-align: middle;
}
.container-mail-rules .rule-detail .well .well-row .actions .btn {
  padding: 0;
  border-radius: 100%;
  text-align: center;
  margin-left: 10px;
  margin-top: 1px;
  width: 24px;
  line-height: 24px;
  height: 24px;
}
.container-mail-rules .rule-detail .well .well-row:last-child {
  border-bottom: none;
}
.container-mail-rules .footer {
  border-top: 1px solid #ffffff;
  background-color: #f6f6f6;
  padding: 15px 12px;
}
.container-mail-rules .footer .btn {
  margin-right: 12px;
}
