css:
.sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #41403b, #34332f);
  box-shadow: none;
}
.sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(255, 255, 255, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(255, 255, 255, 0.8);
}
.preferences-wrap {
  height: 100%;
  background-color: #ffffff;
  color: #231f20;
}
.preferences-wrap input[type=checkbox] {
  margin: 0 7px 0 0;
  position: relative;
  top: -1px;
}
.preferences-wrap input[type=radio] {
  margin: 0 7px 0 0;
  position: relative;
  top: -1px;
}
.preferences-wrap select {
  margin: 4px 0 0 8px;
}
.preferences-wrap h6 {
  color: rgba(35, 31, 32, 0.5);
  margin-top: 20px;
  margin-bottom: 10px;
}
.preferences-wrap section:first-child h6:first-child {
  margin-top: 0;
}
.preferences-wrap p {
  color: rgba(35, 31, 32, 0.5);
  font-size: 12.04px;
}
.preferences-wrap p a {
  color: rgba(35, 31, 32, 0.5);
  font-weight: bold;
  text-decoration: underline;
}
.preferences-wrap *[contenteditable] p {
  color: #231f20;
  font-size: 14px;
}
.preferences-wrap *[contenteditable] p {
  color: #231f20;
  font-size: 14px;
}
.preferences-wrap section {
  padding-bottom: 5px;
}
.preferences-wrap section .item {
  padding-top: 4px;
  padding-bottom: 4px;
}
.preferences-wrap .container-preference-tabs {
  width: 100%;
  background-color: #f6f7f8;
  border-bottom: 1px solid #ffffff;
}
.preferences-wrap .container-preference-tabs .preferences-tabs {
  padding-top: 10px;
  background-color: #f6f7f8;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item {
  cursor: default;
  margin: 0 auto;
  flex: 1.3;
  min-width: 54px;
  max-width: 120px;
  text-align: center;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item:active img {
  -webkit-filter: brightness(40%);
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item.active {
  background: #e6e6e6;
  border-radius: 5px 5px 0 0;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item .tab-icon {
  padding-top: 8px;
  padding-bottom: 8px;
  display: block;
  margin: auto;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item .name {
  padding: 5px 3.6px 9px 3.6px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 13.02px;
  line-height: 13.02px;
}
.preferences-wrap .preferences-content {
  flex: 8;
  background-color: #f9f9f9;
}
.preferences-wrap .preferences-content > .scroll-region-content {
  padding: 27px 48px;
}
.preferences-wrap .preferences-content .container-dropdown {
  margin: 5px 0;
}
.preferences-wrap .preferences-content .container-dropdown .dropdown {
  padding-left: 10px;
}
.preferences-wrap .container-general {
  width: 40%;
  min-width: 400px;
  margin: 0 auto;
}
.preferences-wrap .container-general .local-data .btn {
  margin: 4px 0 0 8px;
  border: 1px solid rgba(230, 230, 230, 0.9);
  box-shadow: none;
  border-radius: 5px;
}
.preferences-wrap .container-appearance {
  width: 400px;
  margin: 0 auto;
}
.preferences-wrap .container-appearance .appearance-mode-switch {
  max-width: 400px;
  text-align: right;
  margin: 10px 0;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode {
  background-color: #fdfdfd;
  border-radius: 10px;
  border: 1px solid #6d7987;
  text-align: center;
  flex: 1;
  padding: 25px;
  padding-bottom: 9px;
  margin-right: 10px;
  margin-bottom: 7px;
  margin-top: 0;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode img {
  background-color: #6d7987;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode div {
  margin-top: 15px;
  text-transform: capitalize;
  cursor: default;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode:last-child {
  margin-right: 0;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode.active {
  border: 1px solid #f07441;
  color: #f07441;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode.active img {
  background-color: #f07441;
}
.preferences-wrap .container-keymaps {
  width: 40%;
  min-width: 460px;
  margin: 0 auto;
}
.preferences-wrap .container-keymaps .col-left {
  text-align: right;
  flex: 1;
  margin-right: 20px;
}
.preferences-wrap .container-keymaps .col-right {
  text-align: left;
  flex: 1;
}
.preferences-wrap .container-keymaps .col-right select {
  width: 75%;
}
.preferences-wrap .container-keymaps .shortcut-section-title {
  border-bottom: 1px solid #ffffff;
  margin: 13.5px 0;
}
.preferences-wrap .container-keymaps .shortcut {
  padding: 3px 0;
  color: rgba(35, 31, 32, 0.5);
}
.preferences-wrap .container-keymaps .shortcut .values {
  font-family: monospace;
  font-weight: 600;
  color: #231f20;
  display: inline-block;
  padding-left: 10px;
  padding-right: 10px;
  cursor: text;
}
.preferences-wrap .container-keymaps .shortcut .values .shortcut-value .then {
  font-size: 0.9em;
  color: rgba(35, 31, 32, 0.5);
}
.preferences-wrap .container-keymaps .shortcut .values .shortcut-value:after {
  content: ", ";
}
.preferences-wrap .container-keymaps .shortcut .values .shortcut-value:last-child:after {
  content: "";
}
.preferences-wrap .container-keymaps .shortcut.editing .values {
  background: #ffffff;
  color: #f07441;
  border-radius: 3px;
  outline: 1px solid rgba(230, 230, 230, 0.9);
}
.preferences-wrap .platform-note {
  padding: 5px 12px;
  background: rgba(35, 31, 32, 0.04);
  border-left: 3px solid #3187e1;
  margin: 5px 0;
  font-size: 0.95em;
}
.preferences-wrap .platform-note:before {
  color: #3187e1;
  font-weight: 600;
  content: "NOTE: ";
}
.preferences-wrap .platform-linux-only {
  display: none;
}
body.platform-win32 .preferences-wrap .well {
  border-radius: 0;
}
body.platform-win32 .preferences-wrap .container-appearance .appearance-mode {
  border-radius: 0;
}
body.platform-linux .preferences-wrap .platform-linux-only {
  display: block;
}
@media (-webkit-min-device-pixel-ratio: 2) {
  .preferences-tabs .tab-icon {
    padding-top: 15px !important;
  }
}
@media (max-width: 600px) {
  .preferences-tabs .item .name {
    display: none;
  }
  .preferences-wrap .preferences-content > .scroll-region-content {
    padding-left: 16px;
    padding-right: 16px;
  }
}
