css:
.sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #41403b, #34332f);
  box-shadow: none;
}
.sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url("../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png") center no-repeat, linear-gradient(to top, rgba(241, 241, 241, 0.75) 0%, rgba(253, 253, 253, 0.75) 100%);
}
.snooze-button {
  order: -104;
}
.snooze-popover {
  color: rgba(35, 31, 32, 0.8);
  display: flex;
  flex-direction: column;
}
.snooze-popover .snooze-row {
  display: flex;
}
.snooze-popover .snooze-row .snooze-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 15px 0;
  cursor: default;
  width: 105px;
  line-height: initial;
  text-align: initial;
}
.snooze-popover .snooze-row .snooze-item img {
  background-color: rgba(35, 31, 32, 0.8);
}
.snooze-popover .snooze-row .snooze-item:hover {
  background-color: #f1f1f1;
  color: rgba(35, 31, 32, 0.9);
}
.snooze-popover .snooze-row .snooze-item:hover img {
  background-color: rgba(35, 31, 32, 0.9);
}
.snooze-popover .snooze-row .snooze-item:active {
  background-color: #e9e9e9;
  color: #231f20;
}
.snooze-popover .snooze-row .snooze-item:active img {
  background-color: #231f20;
}
.snooze-popover .snooze-row .snooze-item + .snooze-item {
  border-left: 1px solid #ffffff;
}
.snooze-popover .snooze-row + .snooze-row {
  border-top: 1px solid #ffffff;
}
.snooze-popover .snooze-input {
  border-top: 1px solid #ffffff;
  padding: 9px 16px;
}
.snooze-popover .snooze-input input {
  margin-bottom: 3px;
}
