css:
.sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #41403b, #34332f);
  box-shadow: none;
}
.sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
html,
body {
  margin: 0;
  height: 100%;
  width: 100%;
  overflow: hidden;
  -webkit-font-smoothing: antialiased;
}
.theme-option {
  position: absolute;
  top: 0;
  margin-top: 4px;
  margin-left: 5px;
  width: 100px;
  height: 60px;
  background-color: #f6f6f6;
  color: #231f20;
  border-radius: 5px;
  text-align: center;
  overflow: hidden;
}
.theme-option.active-true {
  border: 1px solid #3187e1;
  box-shadow: 0 0 4px #9ecaed;
}
.theme-option.active-false {
  border: 1px solid #dddddd;
}
.theme-option .theme-name {
  font-family: "Nylas-Pro", "Helvetica", sans-serif;
  font-size: 12px;
  font-weight: 600;
  margin-top: 7px;
  height: 18px;
  overflow: hidden;
}
.theme-option .swatches {
  padding-left: 27px;
  padding-right: 27px;
  display: flex;
  flex-direction: row;
}
.theme-option .swatches .swatch {
  flex: 1;
  height: 10px;
  width: 10px;
  margin: 4px 2px 4px 2px;
  border-radius: 2px;
  border: 1px solid rgba(0, 0, 0, 0.15);
  background-clip: border-box;
  background-origin: border-box;
}
.theme-option .swatches .swatch.font-color {
  background-color: #231f20;
}
.theme-option .swatches .swatch.active-color {
  background-color: #f07746;
}
.theme-option .swatches .swatch.toolbar-color {
  background-color: #41403b;
}
.theme-option .divider-black {
  position: absolute;
  bottom: 12px;
  height: 1px;
  width: 100%;
  background-color: black;
  opacity: 0.15;
}
.theme-option .divider-white {
  position: absolute;
  z-index: 10;
  bottom: 11px;
  height: 1px;
  width: 100%;
  background-color: white;
  opacity: 0.15;
}
.theme-option .strip {
  position: absolute;
  bottom: 0;
  height: 12px;
  width: 100%;
  background-color: #f6f7f8;
}
