css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 8.25px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 4px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.file-wrap {
  cursor: default;
  display: inline-block;
  position: relative;
  font-size: 10.23px;
  margin: 0 0 11px 11px;
  width: calc(50% - 23px);
  min-width: 320px;
  -webkit-user-drag: element;
}
.file-wrap .inner {
  border-radius: 2px;
  color: #231f20;
  background: #fdfdfd;
  height: 37px;
}
.file-wrap .file-info-wrap {
  display: flex;
  align-items: center;
  padding-left: 6.5px;
  width: 100%;
  height: 100%;
  border: solid 1px rgba(0, 0, 0, 0.09);
  border-right: none;
}
.file-wrap .file-info-wrap .file-icon {
  margin-right: 10px;
  flex-shrink: 0;
}
.file-wrap .file-info-wrap .file-name {
  font-weight: 500;
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  max-width: 235px;
}
.file-wrap .file-info-wrap .file-size {
  margin-left: auto;
  margin-right: 8.25px;
  color: #b8b8b8;
}
.file-wrap .file-action-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  margin-left: auto;
  padding-top: 1px;
  height: 100%;
  width: 37px;
  box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.09);
}
.file-wrap .file-action-icon img {
  background-color: #c7c7c7;
}
.file-wrap .file-action-icon:hover img {
  background-color: #949494;
}
.file-wrap .file-action-icon:active {
  background-color: #f2f2f2;
}
.file-wrap.file-upload .file-name {
  color: rgba(35, 31, 32, 0.5);
}
.file-wrap.file-upload .file-name .uploading {
  color: #231f20;
}
.file-wrap .progress-bar-wrap {
  display: none;
}
.file-wrap .progress-bar-wrap.state-downloading,
.file-wrap .progress-bar-wrap.state-started,
.file-wrap .progress-bar-wrap.state-progress {
  display: block;
}
.file-wrap .progress-bar-wrap.state-completed,
.file-wrap .progress-bar-wrap.state-success {
  display: block;
}
.file-wrap .progress-bar-wrap.state-completed .progress-foreground,
.file-wrap .progress-bar-wrap.state-success .progress-foreground {
  background: #00ac6f;
}
.file-wrap .progress-bar-wrap.state-aborted,
.file-wrap .progress-bar-wrap.state-failed {
  display: block;
}
.file-wrap .progress-bar-wrap.state-aborted .progress-foreground,
.file-wrap .progress-bar-wrap.state-failed .progress-foreground {
  background: #ca2541;
}
.file-wrap .progress-bar-wrap .progress-foreground {
  position: absolute;
  left: 0;
  bottom: 0;
  height: 2px;
  width: 0;
  z-index: 3;
  display: block;
  background: #009ec4;
  border-bottom-left-radius: 4px;
  transition: width .3s linear;
}
.file-wrap .progress-bar-wrap .progress-background {
  position: absolute;
  left: 0;
  bottom: 0;
  height: 2px;
  width: 100%;
  z-index: 2;
  display: block;
  background: #b4babd;
  border-bottom-left-radius: 4px;
  border-bottom-right-radius: 4px;
}
.file-wrap.file-image-wrap {
  position: relative;
  text-align: center;
  display: inline-block;
  vertical-align: top;
  margin-bottom: 11px;
  margin-right: 11px;
  margin-left: 11px;
  width: initial;
  max-width: calc(100% - 30px);
}
.file-wrap.file-image-wrap .progress-foreground,
.file-wrap.file-image-wrap .progress-foreground {
  bottom: -2px;
}
.file-wrap.file-image-wrap .progress-background {
  bottom: -2px;
}
.file-wrap.file-image-wrap .file-action-icon,
.file-wrap.file-image-wrap .file-name-container,
.file-wrap.file-image-wrap .file-name {
  display: none;
}
.file-wrap.file-image-wrap .file-action-icon:active {
  background: none;
  -webkit-filter: brightness(95%);
}
.file-wrap.file-image-wrap:hover .file-action-icon,
.file-wrap.file-image-wrap:hover .file-name-container,
.file-wrap.file-image-wrap:hover .file-name {
  display: block;
}
.file-wrap.file-image-wrap .file-action-icon {
  position: absolute;
  z-index: 2;
  right: -8px;
  top: -8px;
  width: 26px;
  border-radius: 0 0 0 3px;
  box-shadow: none;
}
.file-wrap.file-image-wrap .file-action-icon img {
  background: none;
}
.file-wrap.file-image-wrap .file-preview {
  position: relative;
  z-index: 1;
  overflow: hidden;
}
.file-wrap.file-image-wrap .file-preview .file-name-container {
  cursor: default;
  position: absolute;
  bottom: 0;
  top: 0;
  z-index: 2;
  width: 100%;
  height: 100%;
  min-height: 300px;
  background: linear-gradient(to top, rgba(0, 0, 0, 0.75) 0%, rgba(0, 0, 0, 0) 23%);
  vertical-align: bottom;
  pointer-events: none;
}
.file-wrap.file-image-wrap .file-preview .file-name-container .file-name {
  color: #ffffff;
  left: 11px;
  right: 11px;
  bottom: 11px;
  text-align: left;
  position: absolute;
  z-index: 3;
}
.file-wrap.file-image-wrap .file-preview img {
  position: relative;
  z-index: 1;
  max-width: 100%;
  background: url(../static/images/attachments/transparency-background.png) top left repeat;
  background-size: 8px;
}
body.platform-win32 .file-wrap .inner {
  border-radius: 0;
}
