css:
.send-later .menu {
  width: 250px;
}
.send-later .menu .item .time {
  display: none;
  float: right;
  padding-right: 24px;
}
.send-later .menu .item.selected .time,
.send-later .menu .item:hover .time {
  display: inline-block;
}
.send-later .divider {
  border-top: 1px solid #dddddd;
}
.send-later .custom-section {
  padding: 24px 24px;
}
.send-later .cancel-section {
  padding: 16px 24px;
  padding-bottom: 19.2px;
}
.send-later .cancel-section .btn {
  width: 100%;
}
.btn-send-later .at {
  margin-left: 3px;
}
.send-later-status {
  display: flex;
  align-items: center;
}
.send-later-status .time {
  font-size: 0.9em;
  opacity: 0.62;
  color: #ffd151;
  font-weight: 400;
}
.send-later-status img {
  width: 38px;
  margin-left: 15px;
}
