css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 8.25px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 4px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.search-bar {
  position: relative;
  order: -100;
  overflow: visible;
  z-index: 100;
  width: 450px;
  margin-top: 7.5px;
}
.search-bar .menu .header-container {
  padding: 0;
  height: 23px;
  background-color: transparent;
  border: none;
}
.search-bar .menu .header-container input {
  padding-left: 26px;
  padding-right: 26px;
  width: 100%;
  height: 23px;
  border: 1px solid transparent;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
.search-bar .menu .header-container input.empty {
  text-align: left;
}
.search-bar .menu .header-container input.empty:focus {
  text-align: left;
}
.search-bar .menu .footer-container {
  border: none;
}
.search-bar .menu .content-container {
  position: absolute;
  top: 23px;
  z-index: 2;
  width: 100%;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.15);
}
.search-bar .menu .content-container .item {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}
.search-bar .search-container {
  position: relative;
}
.search-bar .search-container.showing-suggestions .suggestions {
  display: inherit;
}
.search-bar .search-container.showing-suggestions .clear {
  color: rgba(35, 31, 32, 0.8);
  display: inherit;
}
.search-bar .search-container.showing-query .clear {
  display: inherit;
}
.search-bar .search-accessory.search {
  position: absolute;
  top: 8px;
  left: 24px;
}
.search-bar .search-accessory.search.loading {
  top: 4px;
  left: 7px;
  width: 15px;
  height: 15px;
}
.search-bar .search-accessory.clear {
  position: absolute;
  top: 3px;
  right: 4px;
  color: rgba(35, 31, 32, 0.8);
  display: none;
}
body.is-blurred .search-bar .menu .header-container input {
  background: none;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
