css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 8.25px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 4px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.plugins-view-tabs {
  color: rgba(35, 31, 32, 0.8);
  list-style-type: none;
  padding-left: 0;
  cursor: default;
}
.plugins-view-tabs li {
  padding: 24px 32px;
  border-bottom: 1px solid #dddddd;
}
.plugins-view-tabs li.active {
  background: #ffffff;
  color: #e9b500;
}
.plugins-view-tabs li.active img.colorfill {
  background: #e9b500;
}
.plugins-view {
  max-width: 800px;
  margin: 0 auto;
}
.plugins-view .new-package {
  margin-bottom: 50px;
}
.plugins-view .installed,
.plugins-view .explore {
  overflow-y: scroll;
  padding-left: 32px;
  height: 100%;
}
.plugins-view .installed .inner,
.plugins-view .explore .inner {
  max-width: 800px;
}
.plugins-view .installed .inner .search-container,
.plugins-view .explore .inner .search-container {
  margin: 24px 2px;
  justify-content: space-between;
}
.plugins-view .installed input,
.plugins-view .explore input {
  box-sizing: border-box;
  width: 30%;
}
.plugins-view .installed .search,
.plugins-view .explore .search {
  padding-left: 0;
  background-repeat: no-repeat;
  background-image: url("../static/images/search/searchloupe@2x.png");
  background-size: 15px 15px;
  background-position: 7px 4px;
  text-indent: 31px;
}
.plugins-view .installed .empty,
.plugins-view .explore .empty {
  color: rgba(35, 31, 32, 0.5);
  margin-bottom: 48px;
}
.plugins-view .package-set {
  margin-top: 35px;
}
.plugins-view .package {
  align-items: center;
  background: #ffffff;
  border: 1px solid #dddddd;
  border-radius: 10px;
  margin-top: 24px;
  margin-bottom: 24px;
  padding: 24px 32px;
}
.plugins-view .package .icon-container {
  width: 52px;
  height: 52px;
  border-radius: 6px;
  background: linear-gradient(to bottom, #ffffff 0%, #f6f6f6 100%);
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 0.5px 1px rgba(0, 0, 0, 0.15);
  flex-shrink: 0;
  margin-right: 32px;
  text-align: center;
  line-height: 50px;
}
.plugins-view .package .info {
  max-width: 380px;
  cursor: default;
}
.plugins-view .package .info .title {
  color: #434648;
  font-size: 14.19px;
  font-weight: 400;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.plugins-view .package .info .version {
  font-size: 10.23px;
  font-weight: 400;
  margin-left: 10px;
  margin-top: 4px;
}
.plugins-view .package .info .uninstall-plugin {
  color: #419bf9;
  margin-left: 10px;
  margin-top: 4px;
}
.plugins-view .package .info .description {
  padding-top: 16px;
  color: rgba(35, 31, 32, 0.5);
  font-size: 10.23px;
}
.plugins-view .package .actions {
  flex: 1;
  text-align: right;
}
.plugins-view .package .actions .btn {
  margin-left: 16px;
}
.plugins-view .package .update-info {
  background: rgba(255, 209, 81, 0.1);
  line-height: 17.6px;
}
.plugins-view .package .update-info .btn {
  float: right;
}
