@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 200;
  src: url('nylas://nylas-private-fonts/fonts/Nylas-Pro-Thin.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 300;
  src: url('nylas://nylas-private-fonts/fonts/Nylas-Pro-Blond.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 400;
  src: url('nylas://nylas-private-fonts/fonts/Nylas-Pro-Normal.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 500;
  src: url('nylas://nylas-private-fonts/fonts/Nylas-Pro-Medium.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 600;
  src: url('nylas://nylas-private-fonts/fonts/Nylas-Pro-SemiBold.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 600;
  src: url('nylas://nylas-private-fonts/fonts/Nylas-Pro-Normal.otf'), Helvetica, sans-serif;
  unicode-range: U+1F300-1F5FF, U+1F600-1F64F, U+1F680-1F6FF, U+2600-26FF;
}
.ignore-in-parent-frame html,
.ignore-in-parent-frame body {
  font-family: "Nylas-Pro", "Helvetica", "Lucidia Grande", sans-serif;
  font-size: 14.5px;
  line-height: 1.5;
  color: #231f20;
  background-color: transparent !important;
  border: 0;
  margin: 0;
  padding: 0;
  overflow-x: auto;
  -webkit-text-size-adjust: auto;
  word-wrap: break-word;
  -webkit-nbsp-mode: space;
  -webkit-line-break: after-white-space;
}
.ignore-in-parent-frame strong,
.ignore-in-parent-frame b,
.ignore-in-parent-frame .bold {
  font-weight: 600;
}
.ignore-in-parent-frame body {
  padding: 0;
  margin: auto;
  max-width: 840px;
  overflow-y: hidden;
  word-break: break-word;
  -webkit-font-smoothing: antialiased;
}
.ignore-in-parent-frame a {
  color: #419bf9;
}
.ignore-in-parent-frame a:hover {
  color: #2d7cd0;
}
.ignore-in-parent-frame a:visited {
  color: #1081f7;
}
.ignore-in-parent-frame a img {
  border-bottom: 0;
}
.ignore-in-parent-frame body.heightDetermined {
  overflow-y: hidden;
}
.ignore-in-parent-frame div,
.ignore-in-parent-frame pre {
  max-width: 100%;
}
.ignore-in-parent-frame img {
  border: 0;
}
.ignore-in-parent-frame search-match,
.ignore-in-parent-frame .search-match {
  background: #fff000;
  border-radius: 8px;
  box-shadow: 0 0.5px 0.5px rgba(0, 0, 0, 0.25);
}
.ignore-in-parent-frame search-match.current-match,
.ignore-in-parent-frame .search-match.current-match {
  background: #ff8b1a;
}
