css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 8.25px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 4px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.event-wrapper {
  cursor: default;
  position: relative;
  font-size: 10.23px;
  margin-top: 11px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 100%;
  display: inline-block;
  border-radius: 8px;
  border: 1px solid #dddddd;
}
.event-wrapper .event-header {
  border-bottom: 1px solid #dddddd;
  padding: 10px;
  overflow: hidden;
  text-overflow: ellipsis;
}
.event-wrapper .event-header img {
  margin-right: 20px;
}
.event-wrapper .event-header .event-title-text {
  color: rgba(35, 31, 32, 0.5);
}
.event-wrapper .event-header .event-title {
  color: #231f20;
}
.event-wrapper .event-body {
  padding: 16px;
}
.event-wrapper .event-body .event-date {
  display: inline-block;
  width: 100%;
}
.event-wrapper .event-body .event-date .event-day {
  display: block;
  font-size: 12.54px;
  color: #e64d65;
}
.event-wrapper .event-body .event-date .event-time {
  display: inline-block;
  font-size: 15.73px;
  font-weight: 300;
}
.event-wrapper .event-body .event-actions {
  display: inline-block;
  float: right;
  z-index: 4;
  text-align: center;
}
.event-wrapper .event-body .event-actions .btn-rsvp {
  float: left;
  padding: 8.25px 19.25px 8.25px 19.25px;
  line-height: 10px;
  color: #231f20;
  border-radius: 3px;
  background: #ffffff;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.15);
  margin: 0 7.5px 0 7.5px;
}
.event-wrapper .event-body .event-actions .btn-rsvp:active {
  background: transparent;
}
.event-wrapper .event-body .event-actions .btn-rsvp.no {
  background: #d9534f;
  color: #ffffff;
}
.event-wrapper .event-body .event-actions .btn-rsvp.yes {
  background: #6dd474;
  color: #ffffff;
}
.event-wrapper .event-body .event-actions .btn-rsvp.maybe {
  background: #77828d;
  color: #ffffff;
}
body.platform-win32 .event-wrapper {
  border-radius: 0;
}
body.platform-win32 .event-wrapper .event-body .event-actions .btn-rsvp {
  border-radius: 0;
}
