css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 8.25px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 4px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.translate-language-picker .footer-container {
  display: none;
}
.translate-language-picker .content-container {
  height: 185px;
  width: 170px;
  overflow: scroll;
}
