css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.related-threads {
  width: calc(100% + 30px);
  position: relative;
  left: -15px;
  border-top: 1px solid rgba(0, 0, 0, 0.15);
  transition: height 150ms ease-in-out;
  top: 15px;
  margin-top: -15px;
  overflow: hidden;
  border-radius: 0 0 5px 5px;
}
.related-threads .related-thread {
  display: flex;
  font-size: 12px;
  color: rgba(192, 198, 203, 0.6);
  width: 100%;
  padding: 0.5em 15px;
  border-top: 1px solid rgba(0, 0, 0, 0.08);
}
.related-threads .related-thread:hover {
  background: #292929;
}
.related-threads .related-thread .content {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  padding-right: 1em;
}
.related-threads .related-thread .content .snippet {
  opacity: 0.5;
}
.related-threads .toggle {
  font-size: 12px;
  text-align: center;
  padding: 0.5em 15px;
  border-top: 1px solid rgba(0, 0, 0, 0.08);
  color: #419bf9;
}
.participant-profile {
  margin-bottom: 24px;
}
.participant-profile .profile-photo-wrap {
  width: 50px;
  height: 50px;
  border-radius: 3px;
  padding: 3px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.5);
  position: absolute;
  left: calc(50% - 25px);
  top: -31px;
  background: #333539;
}
.participant-profile .profile-photo-wrap .profile-photo {
  border-radius: 2px;
  overflow: hidden;
  text-align: center;
  width: 44px;
  height: 44px;
}
.participant-profile .profile-photo-wrap .profile-photo img,
.participant-profile .profile-photo-wrap .profile-photo .default-profile-image {
  width: 44px;
  height: 44px;
}
.participant-profile .profile-photo-wrap .profile-photo .default-profile-image {
  line-height: 44px;
  font-size: 18px;
  font-weight: 500;
  color: white;
  box-shadow: inset 0 0 1px rgba(0, 0, 0, 0.18);
  background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0) 100%);
}
.participant-profile .core-personal-info {
  padding-top: 30px;
  text-align: center;
  margin-bottom: 14px;
}
.participant-profile .core-personal-info .full-name,
.participant-profile .core-personal-info .email {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.participant-profile .core-personal-info .full-name {
  font-size: 16px;
}
.participant-profile .core-personal-info .email {
  color: rgba(192, 198, 203, 0.6);
  margin-bottom: 14px;
}
.participant-profile .core-personal-info .social-profiles-wrap {
  margin-bottom: 14px;
}
.participant-profile .core-personal-info .social-profile-item {
  margin: 0 10px;
}
.participant-profile .additional-info {
  font-size: 12px;
}
.participant-profile .additional-info p {
  margin-bottom: 15px;
}
.participant-profile .additional-info .bio {
  color: rgba(192, 198, 203, 0.6);
}
body.platform-win32 .participant-profile {
  border-radius: 0;
}
body.platform-win32 .participant-profile .profile-photo {
  border-radius: 0;
}
body.platform-win32 .related-threads {
  border-radius: 0;
}
