css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.container-mail-rules {
  max-width: 800px;
  margin: 0 auto;
}
.container-mail-rules .empty-list {
  height: 376px;
  width: inherit;
  background-color: #333539;
  border: 1px solid #313236;
  text-align: center;
}
.container-mail-rules .empty-list .icon-mail-rules {
  margin-top: 80px;
}
.container-mail-rules .empty-list h2 {
  color: rgba(192, 198, 203, 0.6);
}
.container-mail-rules .empty-list .btn {
  margin-top: 10px;
}
.container-mail-rules .rule-list {
  position: relative;
  height: inherit;
  width: inherit;
}
.container-mail-rules .rule-list .items-wrapper {
  min-width: 200px;
  height: 350px;
}
.container-mail-rules .rule-list .item-rule-disabled {
  color: #d9534f;
  padding: 4px 10px;
  border-bottom: 1px solid #313236;
}
.container-mail-rules .rule-list .selected .item-rule-disabled {
  color: #333539;
}
.container-mail-rules .rule-detail {
  flex: 1;
  cursor: default;
  background-color: #333539;
  border: 1px solid #313236;
  border-left: 0;
}
.container-mail-rules .rule-detail .disabled-reason {
  padding: 10px 10px;
  background-color: rgba(202, 37, 65, 0.3);
  border-bottom: 1px solid #ca2541;
  margin-bottom: 5px;
}
.container-mail-rules .rule-detail .disabled-reason .btn {
  margin-left: 24px;
  float: right;
}
.container-mail-rules .rule-detail .inner {
  padding: 5px 12px;
}
.container-mail-rules .rule-detail .no-selection {
  color: rgba(192, 198, 203, 0.6);
  text-align: center;
  padding: 100px;
}
.container-mail-rules .rule-detail .well {
  background-color: #333539;
  border: 1px solid #313236;
  margin: 5px 0;
  font-size: 0.9em;
}
.container-mail-rules .rule-detail .well .well-row {
  padding: 5px 12px;
  border-bottom: 1px solid #313236;
}
.container-mail-rules .rule-detail .well .well-row select,
.container-mail-rules .rule-detail .well .well-row input {
  margin: 1.25px 6px;
}
.container-mail-rules .rule-detail .well .well-row select:first-child,
.container-mail-rules .rule-detail .well .well-row input:first-child {
  margin-left: 0;
}
.container-mail-rules .rule-detail .well .well-row select:last-child,
.container-mail-rules .rule-detail .well .well-row input:last-child {
  margin-right: 0;
}
.container-mail-rules .rule-detail .well .well-row select {
  max-width: 170px;
}
.container-mail-rules .rule-detail .well .well-row input {
  width: 200px;
}
.container-mail-rules .rule-detail .well .well-row .actions {
  white-space: nowrap;
  vertical-align: middle;
}
.container-mail-rules .rule-detail .well .well-row .actions .btn {
  padding: 0;
  border-radius: 100%;
  text-align: center;
  margin-left: 10px;
  margin-top: 1px;
  width: 24px;
  line-height: 24px;
  height: 24px;
}
.container-mail-rules .rule-detail .well .well-row:last-child {
  border-bottom: none;
}
.container-mail-rules .footer {
  border-top: 1px solid #313236;
  background-color: #333539;
  padding: 15px 12px;
}
.container-mail-rules .footer .btn {
  margin-right: 12px;
}
