css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.trial-remaining-block {
  text-align: center;
  box-shadow: inset 0 0 1px 1px rgba(0, 0, 0, 0.05);
  display: flex;
  flex-direction: column;
  padding: 20px 20px;
  background: -webkit-linear-gradient(top, #41c190 0%, #41b78c 30%);
  color: #ffffff;
  font-size: 15.96px;
}
.trial-remaining-block .learn-more {
  border: 0;
  color: rgba(255, 255, 255, 0.8);
  font-size: 12.04px;
  padding-bottom: 12px;
  padding-top: 2px;
}
.trial-remaining-block .learn-more span {
  white-space: nowrap;
}
.trial-remaining-block .learn-more span:after {
  content: '';
  background: url(nylas://notifications/assets/minichevron@2x.png) top left no-repeat;
  background-size: 4.5px 7px;
  margin-left: 3px;
  display: inline-block;
  width: 4.5px;
  height: 7px;
  vertical-align: baseline;
}
.trial-remaining-block .learn-more:active {
  color: rgba(255, 255, 255, 0.5);
}
.trial-remaining-block .btn.subscribe {
  border-radius: 5px;
  color: #ffffff;
  background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.1) 80%);
  border: solid rgba(255, 255, 255, 0.6) 1px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.15);
  padding: 0 12px;
  cursor: pointer;
  width: 160px;
  margin: auto;
  display: block;
  max-width: 90%;
}
.trial-remaining-block .btn.subscribe:active {
  background: -webkit-linear-gradient(top, rgba(192, 198, 203, 0.2) 0%, rgba(192, 198, 203, 0.1) 80%);
}
