css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
body.platform-win32 .category-picker-popover {
  margin-left: 0;
}
.sheet-toolbar .btn-category-picker:only-of-type {
  margin-right: 0;
}
.category-picker-popover .menu {
  background: #333539;
  width: 250px;
  max-height: 400px;
}
.category-picker-popover .menu .header-container {
  border-bottom: 0;
}
.category-picker-popover .menu .item.divider {
  background-color: #e0e0e0;
  margin: 4px 0;
  height: 2px;
  padding: 0;
}
.category-picker-popover .btn.btn-toolbar {
  margin-left: 0;
  margin-right: 0;
}
.category-picker-popover .check-wrap {
  width: 14px;
  height: 14px;
  border-radius: 2px;
  display: inline-block;
  position: relative;
  flex-shrink: 0;
  top: 2px;
}
.category-picker-popover .item img.content-mask {
  position: relative;
  top: 3px;
  background-color: rgba(192, 198, 203, 0.8);
}
.category-picker-popover .item.selected img.content-mask,
.category-picker-popover .item.active img.content-mask {
  background-color: #ffffff;
}
.category-picker-popover img.check-img {
  position: absolute;
}
.category-picker-popover .category-item {
  font-size: 14px;
  display: flex;
}
.category-picker-popover .category-create-new-tag {
  flex-shrink: 0;
}
.category-picker-popover .category-display-name {
  display: inline-block;
  margin-left: 10px;
  margin-right: 5px;
  word-break: break-word;
  flex: 1;
}
