css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(49, 50, 54, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(49, 50, 54, 0.8);
}
.undo-redo-manager {
  position: absolute;
  bottom: 10px;
  z-index: 100;
  width: 100%;
  text-align: center;
  pointer-events: none;
}
.undo-redo {
  height: 45px;
  display: inline-block;
  padding-top: 12px;
  opacity: 0.9;
  border-radius: 3px;
  background: #dddddd;
  white-space: nowrap;
  cursor: default;
  pointer-events: initial;
  display: inline-flex;
  flex-direction: row;
  max-width: 90%;
}
.undo-redo .undo-redo-message-wrapper {
  flex: 1;
  flex-shrink: 1;
  margin-left: 16px;
  margin-right: 30px;
  color: #333539;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: left;
}
.undo-redo .undo-redo-action-wrapper {
  flex-shrink: 0;
  margin-right: 15px;
  white-space: nowrap;
}
.undo-redo .undo-redo-action-wrapper img {
  background-color: #333539;
}
.undo-redo .undo-redo-action-wrapper .undo-redo-action-text {
  margin-left: 5px;
  color: #333539;
}
body.platform-win32 .undo-redo {
  border-radius: 0;
}
.undo-redo-item-enter {
  opacity: 0.01;
  transform: translate3d(0, 10px, 0);
  transition: all 0.15s ease-out;
}
.undo-redo-item-enter.undo-redo-item-enter-active {
  opacity: 1;
  transform: translate3d(0, 0, 0);
}
.undo-redo-item-leave {
  opacity: 1;
  transform: translate3d(0, 0, 0);
  transition: all 0.15s ease-in;
}
.undo-redo-item-leave.undo-redo-item-leave-active {
  transform: translate3d(0, 10px, 0);
  opacity: 0.01;
}
