css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.find-in-thread {
  background: #333539;
  text-align: right;
  overflow: hidden;
  height: 0;
  padding: 0 8px;
  transition: all 125ms ease-in-out;
  border-bottom: 0;
}
.find-in-thread.enabled {
  padding: 4px 8px;
  height: 35px;
  border-bottom: 1px solid #313236;
}
.find-in-thread .controls-wrap {
  display: inline-block;
}
.find-in-thread .selection-progress {
  color: rgba(192, 198, 203, 0.6);
  position: absolute;
  top: 4px;
  right: 54px;
  font-size: 12px;
}
.find-in-thread .btn.btn-find-in-thread {
  border: 0;
  box-shadow: 0 0 0;
  border-radius: 0;
  background: transparent;
  display: inline-block;
}
.find-in-thread .input-wrap {
  display: inline-block;
  position: relative;
}
.find-in-thread .input-wrap input {
  height: 26px;
  width: 230px;
  padding-left: 8px;
  font-size: 12px;
}
.find-in-thread .input-wrap .btn-wrap {
  width: 54px;
  position: absolute;
  top: 0;
  right: 0;
}
