css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.mail-merge-workspace {
  width: 100%;
  height: 250px;
  z-index: 1;
  border-top: 1px solid lightgrey;
  padding: 10.8px 19.2px;
}
.mail-merge-workspace .selection-controls {
  display: flex;
  align-items: center;
  color: rgba(192, 198, 203, 0.6);
  margin-bottom: 10px;
}
.mail-merge-workspace .selection-controls .btn.btn-group {
  padding: 0;
}
.mail-merge-workspace .selection-controls .btn.btn-group:active {
  background: initial;
}
.mail-merge-workspace .selection-controls .btn {
  display: flex;
  align-items: center;
  height: 1.5em;
  margin-right: 5px;
  color: rgba(192, 198, 203, 0.6);
}
.mail-merge-workspace .selection-controls .btn:hover img {
  background-color: rgba(192, 198, 203, 0.8);
}
.mail-merge-workspace .selection-controls .btn .btn-prev,
.mail-merge-workspace .selection-controls .btn .btn-next {
  height: 100%;
  width: 15px;
}
.mail-merge-workspace .selection-controls .btn .btn-prev img,
.mail-merge-workspace .selection-controls .btn .btn-next img {
  background-color: rgba(192, 198, 203, 0.6);
}
.mail-merge-workspace .selection-controls .btn .btn-prev:active,
.mail-merge-workspace .selection-controls .btn .btn-next:active {
  background: #292b2e;
}
.mail-merge-workspace .selection-controls .btn .btn-prev img {
  transform: rotate(90deg) translate(-9px, -8px);
}
.mail-merge-workspace .selection-controls .btn .btn-next img {
  transform: rotate(-90deg) translate(9px, 8px);
}
.mail-merge-workspace .mail-merge-table {
  height: 90%;
}
.mail-merge-workspace .mail-merge-table .editable-table-container {
  width: 100%;
}
.mail-merge-workspace .mail-merge-table .editable-table-container > .key-commands-region {
  width: initial;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table {
  max-width: 700px;
  font-size: 0.9em;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .table-row-header.selected,
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .table-row-header th,
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .table-row-header th.selected {
  background: initial;
  border: 1px solid #3d3f44;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .table-row.table-row-header {
  height: 35px;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .table-row {
  height: 30px;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .numbered-cell {
  width: 30px;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table th.table-cell .mail-merge-token input {
  cursor: -webkit-grab;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table td.table-cell input {
  cursor: default;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .table-cell:not(.numbered-cell) {
  width: 120px;
}
.mail-merge-workspace .mail-merge-table .nylas-table.editable-table .header-cell {
  display: flex;
  align-items: center;
  height: 28px;
  padding-left: 6px;
}
.token-color-0.mail-merge-token {
  background-color: #ebf5fa;
  color: #528598;
  border-color: #cbe6f1;
}
.token-color-0.mail-merge-token img {
  background-color: #528598;
}
.mail-merge-participants-text-field .token.token-color-0.selected,
.mail-merge-participants-text-field .token.token-color-0.dragging {
  border-color: #528598;
}
.token-color-1.mail-merge-token {
  background-color: #ebf1fa;
  color: #526d98;
  border-color: #cbd9f1;
}
.token-color-1.mail-merge-token img {
  background-color: #526d98;
}
.mail-merge-participants-text-field .token.token-color-1.selected,
.mail-merge-participants-text-field .token.token-color-1.dragging {
  border-color: #526d98;
}
.token-color-2.mail-merge-token {
  background-color: #ebecfa;
  color: #525698;
  border-color: #cbcdf1;
}
.token-color-2.mail-merge-token img {
  background-color: #525698;
}
.mail-merge-participants-text-field .token.token-color-2.selected,
.mail-merge-participants-text-field .token.token-color-2.dragging {
  border-color: #525698;
}
.token-color-3.mail-merge-token {
  background-color: #efebfa;
  color: #665298;
  border-color: #d6cbf1;
}
.token-color-3.mail-merge-token img {
  background-color: #665298;
}
.mail-merge-participants-text-field .token.token-color-3.selected,
.mail-merge-participants-text-field .token.token-color-3.dragging {
  border-color: #665298;
}
.token-color-4.mail-merge-token {
  background-color: #f4ebfa;
  color: #7e5298;
  border-color: #e2cbf1;
}
.token-color-4.mail-merge-token img {
  background-color: #7e5298;
}
.mail-merge-participants-text-field .token.token-color-4.selected,
.mail-merge-participants-text-field .token.token-color-4.dragging {
  border-color: #7e5298;
}
.mail-merge-token {
  border: 1px solid;
  border-radius: 5px;
  padding-left: 7px;
  cursor: -webkit-grab;
}
.mail-merge-token-wrap,
.n1-overlaid-component-anchor-container.mail-merge-token-wrap {
  margin: 0 1px;
  vertical-align: bottom;
}
.mail-merge-token-wrap .mail-merge-token,
.n1-overlaid-component-anchor-container.mail-merge-token-wrap .mail-merge-token {
  padding: 2px 5px;
  padding-right: 2px;
}
.mail-merge-token-wrap .mail-merge-token img,
.n1-overlaid-component-anchor-container.mail-merge-token-wrap .mail-merge-token img {
  margin-right: 10px;
}
.header-cell .mail-merge-token {
  display: flex;
  align-items: center;
  height: 22px;
}
.header-cell .mail-merge-token input {
  max-width: 90%;
  text-align: left;
  height: 100%;
  line-height: 100%;
  color: inherit;
  padding-bottom: 2px;
}
.mail-merge-participants-text-field .token {
  box-shadow: none;
  padding: 0;
  border-radius: 5px;
}
.mail-merge-participants-text-field .token .mail-merge-token {
  display: flex;
  align-items: center;
  padding: 0 5px;
  font-size: 0.9em;
}
.mail-merge-participants-text-field .token .mail-merge-token img {
  margin-right: 10px;
}
.mail-merge-participants-text-field .token.selected,
.mail-merge-participants-text-field .token.dragging {
  background: none;
  border-radius: 5px;
}
.mail-merge-subject-overlaid .toggle-preview {
  top: 2px;
  right: 22px;
}
.mail-merge-subject-text-field div[contenteditable] {
  line-height: 21px;
  font-weight: 400;
  padding: 13px 0 9px 0;
  min-width: 5em;
  background-color: transparent;
  border: none;
  margin: 0;
}
.mail-merge-subject-text-field div[contenteditable]:empty:before {
  content: attr(placeholder);
  color: rgba(192, 198, 203, 0.6);
}
