/**
 * Borders
 */
.fixed-popover .thread-sharing-popover {
  position: relative;
  width: 265px;
}
.fixed-popover .thread-sharing-popover .share-toggle {
  padding: 7px 10px;
}
.fixed-popover .thread-sharing-popover .share-toggle input {
  margin-right: 7px;
}
.fixed-popover .thread-sharing-popover .share-input {
  border-top: 2px solid rgba(0, 0, 0, 0.15);
  padding: 10px 5px 0 5px;
}
.fixed-popover .thread-sharing-popover .share-input input[type="text"] {
  cursor: default;
  -webkit-user-select: all;
}
.fixed-popover .thread-sharing-popover .share-controls {
  text-align: center;
  padding: 10px;
}
.fixed-popover .thread-sharing-popover .share-controls .share-message {
  color: #87939a;
  margin-bottom: 10px;
  font-size: 12.04px;
}
.fixed-popover .thread-sharing-popover .share-controls button.btn + button.btn {
  margin-left: 10px;
}
.fixed-popover .thread-sharing-popover.disabled .share-input input[type="text"] {
  color: #87939a;
  -webkit-user-select: none;
}
.fixed-popover .thread-sharing-popover.disabled button.btn {
  color: #87939a;
}
