css:
/**
 * Colors
 */
/**
 * Borders
 */
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(230, 230, 230, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(230, 230, 230, 0.8);
}
.open-tracking-icon img {
  vertical-align: initial;
}
.open-tracking-icon img.content-mask.unopened {
  background-color: rgba(124, 25, 204, 0.2);
  cursor: default;
}
.open-tracking-icon img.content-mask.opened {
  background-color: #7c19cc;
  cursor: pointer;
}
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: rgba(135, 147, 154, 0.3);
}
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #87939a;
}
.open-tracking-icon .open-count {
  display: inline-block;
  position: relative;
  left: -16px;
  text-align: center;
  background-color: #419bf9;
  font-size: 12px;
  font-weight: bold;
}
.open-tracking-icon {
  width: 15px;
  margin: 0 2px;
}
.open-tracking-message-status {
  color: #87939a;
  margin-left: 10px;
}
.open-tracking-message-status.unopened img.content-mask {
  background-color: #87939a;
}
.open-tracking-message-status.opened {
  cursor: pointer;
}
.open-tracking-message-status.opened img.content-mask {
  background-color: #7c19cc;
}
.open-tracking-message-popover {
  width: 200px;
  max-height: 240px;
}
.open-tracking-message-popover .open-tracking-header {
  padding: 5px 12px 0 12px;
  text-align: center;
  color: #9da4aa;
  font-weight: 600;
}
.open-tracking-message-popover .open-history-container {
  max-height: 216px;
  padding: 0 12px 5px 12px;
  overflow: auto;
}
.open-tracking-message-popover .open-history-container .open-action {
  color: #9da4aa;
}
.open-tracking-message-popover .open-history-container .open-action .recipient {
  text-overflow: ellipsis;
  overflow: hidden;
}
.open-tracking-message-popover .open-history-container .open-action .spacer {
  flex: 1 1 0;
}
.open-tracking-message-popover .open-history-container .open-action .timestamp {
  color: #87939a;
  flex-shrink: 0;
}
