css:
/**
 * Colors
 */
/**
 * Borders
 */
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(230, 230, 230, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(230, 230, 230, 0.8);
}
.sidebar-activity-transition-wrapper {
  order: 2;
  z-index: 2;
  overflow-y: auto;
}
.sidebar-activity-transition-wrapper.sidebar-activity-empty {
  position: absolute;
  bottom: 0;
  width: 100%;
}
.sidebar-activity {
  display: block;
  width: 100%;
  bottom: 0;
  background: #fdfdfd;
  font-size: 13.02px;
  color: #9da4aa;
  line-height: 19.95px;
  box-shadow: inset 0 1px 0 #e6e6e6;
  transition: height 0.4s;
  transition-delay: 2s;
}
.sidebar-activity:hover {
  cursor: default;
}
.sidebar-activity .item {
  border-bottom: 1px solid #e6e6e6;
  position: relative;
  opacity: 1;
}
.sidebar-activity .item:hover {
  cursor: default;
}
.sidebar-activity .item .inner {
  padding: 10.8px 12px 10.8px 12px;
}
.sidebar-activity .item .count {
  color: #87939a;
  float: right;
}
.sidebar-activity .item .btn {
  display: block;
  text-align: center;
  margin-top: 4px;
  margin-bottom: 4px;
  font-size: 13.02px;
}
.sidebar-activity .item .progress-track {
  display: block;
  height: 3px;
  font-size: 0;
  background: rgba(0, 0, 0, 0.1);
}
.sidebar-activity .item .progress-track .progress {
  transition: width 0.4s;
  height: 3px;
  background-color: #009ec4;
}
.sidebar-activity .item .account-detail-area {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.2s;
}
.sidebar-activity .item.expanded-sync .account-detail-area {
  max-height: 1000px;
}
.sidebar-activity .item.expanded-sync .account.inner {
  border-top: 1px solid rgba(0, 0, 0, 0.1);
}
.sidebar-activity .item h2 {
  font-size: 14px;
  margin: 0;
  overflow: hidden;
  text-overflow: ellipsis;
  font-weight: 500;
}
.sidebar-activity .item h3 {
  font-size: 14px;
  margin: 10px 0 4px 0;
}
.sidebar-activity .item .amount {
  margin-top: 2px;
  font-size: 12px;
  color: #9da4aa;
}
.sidebar-activity .item .close-expanded {
  display: block;
  margin: 0 12px 9px 12px;
}
.sidebar-activity .item .complete .progress-track .progress {
  background-color: #5cb346;
}
.sidebar-activity.sidebar-activity-error .progress {
  background-color: #d9534f;
}
.activity-opacity-enter {
  opacity: 0;
  transition: opacity 0.125s ease-out;
}
.activity-opacity-enter.activity-opacity-enter-active {
  opacity: 1;
}
.activity-opacity-leave {
  opacity: 1;
  transition: opacity 0.125s ease-in;
  transition-delay: 0.5s;
}
.activity-opacity-leave.activity-opacity-leave-active {
  transition-delay: 0.5s;
  opacity: 0;
}
.notifications-sticky {
  width: 100%;
}
.notifications-sticky .notification-info {
  background-color: #009ec4;
}
.notifications-sticky .notification-developer {
  background-color: #615396;
}
.notifications-sticky .notification-upgrade {
  background-image: -webkit-linear-gradient(bottom, #429e91, #40b1ac);
}
.notifications-sticky .notification-upgrade img {
  background-color: #87939a;
}
.notifications-sticky .notification-error {
  background: linear-gradient(to top, #b9223b 0%, #ca2541 100%);
  border-color: #ca2541;
  color: #d9534f;
}
.notifications-sticky .notification-offline {
  background: linear-gradient(to top, #b88a00 0%, #cc9900 100%);
  border-color: #b38600;
}
.notifications-sticky .notifications-sticky-item {
  display: flex;
  font-size: 14px;
  color: #87939a;
  border-bottom: 1px solid rgba(0, 0, 0, 0.25);
  padding-left: 12px;
  line-height: 2.25;
  align-items: baseline;
}
.notifications-sticky .notifications-sticky-item a {
  flex-shrink: 0;
  color: #87939a;
  padding: 0 12px;
}
.notifications-sticky .notifications-sticky-item a:hover {
  background-color: rgba(255, 255, 255, 0.15);
  text-decoration: none;
  color: #87939a;
}
.notifications-sticky .notifications-sticky-item a.default {
  background-color: rgba(0, 0, 0, 0.15);
}
.notifications-sticky .notifications-sticky-item a.default:hover {
  background-color: rgba(255, 255, 255, 0.15);
}
.notifications-sticky .notifications-sticky-item i {
  margin-right: 12px;
}
.notifications-sticky .notifications-sticky-item .icon {
  display: inline-block;
  align-self: center;
  line-height: 16px;
  margin-right: 12px;
}
.notifications-sticky .notifications-sticky-item .icon img {
  vertical-align: initial;
}
.notifications-sticky .notifications-sticky-item div.message {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  line-height: 1.65;
  padding: 4px 0;
}
.notifications-sticky .notifications-sticky-item.has-default-action:hover {
  -webkit-filter: brightness(110%);
  cursor: default;
}
body.platform-win32 .notifications-sticky .notifications-sticky-item a {
  border-radius: 0;
}
