css:
@import "font-awesome.min.css";
/**
 * Colors
 */
/**
 * Borders
 */
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(230, 230, 230, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(230, 230, 230, 0.8);
}
* {
  box-sizing: border-box;
}
*:before,
*:after {
  box-sizing: border-box;
}
input,
button,
select,
textarea {
  font-family: inherit;
  font-size: inherit;
  line-height: inherit;
}
figure {
  margin: 0;
}
img {
  vertical-align: middle;
  border: 0;
}
/*! normalize.css v3.0.1 | MIT License | git.io/normalize */
html {
  font-family: sans-serif;
  -ms-text-size-adjust: 100%;
  -webkit-text-size-adjust: 100%;
}
body {
  margin: 0;
}
article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
main,
nav,
section,
summary {
  display: block;
}
audio,
canvas,
progress,
video {
  display: inline-block;
  vertical-align: baseline;
}
audio:not([controls]) {
  display: none;
  height: 0;
}
a {
  background: transparent;
}
a:active,
a:hover {
  outline: 0;
}
abbr[title] {
  border-bottom: 1px dotted;
}
b,
strong {
  font-weight: bold;
}
dfn {
  font-style: italic;
}
h1 {
  font-size: 2em;
  margin: 0.67em 0;
}
mark {
  background: #ff0;
  color: #000;
}
small {
  font-size: 80%;
}
sub,
sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}
sup {
  top: -0.5em;
}
sub {
  bottom: -0.25em;
}
svg:not(:root) {
  overflow: hidden;
}
hr {
  -moz-box-sizing: content-box;
  box-sizing: content-box;
  height: 0;
}
pre {
  overflow: auto;
}
code,
kbd,
pre,
samp {
  font-family: monospace, monospace;
  font-size: 1em;
}
button,
input,
optgroup,
select,
textarea {
  color: inherit;
  font: inherit;
  margin: 0;
}
button {
  overflow: visible;
}
button,
select {
  text-transform: none;
}
button,
html input[type="button"] {
  -webkit-appearance: button;
  cursor: pointer;
}
button[disabled],
html input[disabled] {
  cursor: default;
}
button input::-moz-focus-inner {
  border: 0;
  padding: 0;
}
input {
  line-height: normal;
}
input[type="reset"],
input[type="submit"] {
  -webkit-appearance: button;
  cursor: pointer;
}
input[type="checkbox"],
input[type="radio"] {
  box-sizing: border-box;
  padding: 0;
}
input[type="number"]::-webkit-inner-spin-button,
input[type="number"]::-webkit-outer-spin-button {
  height: auto;
}
input[type="search"] {
  -webkit-appearance: textfield;
  -moz-box-sizing: content-box;
  -webkit-box-sizing: content-box;
  box-sizing: content-box;
}
input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-decoration {
  -webkit-appearance: none;
}
fieldset {
  border: 1px solid #c0c0c0;
  margin: 0 2px;
  padding: 0.35em 0.625em 0.75em;
}
legend {
  border: 0;
  padding: 0;
}
textarea {
  overflow: auto;
}
optgroup {
  font-weight: bold;
}
table {
  border-collapse: collapse;
  border-spacing: 0;
}
td,
th {
  padding: 0;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: "Nylas-Pro", "Helvetica", sans-serif;
  line-height: 1.1;
  color: #434648;
}
h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small,
h1 .small,
h2 .small,
h3 .small,
h4 .small,
h5 .small,
h6 .small {
  line-height: 1;
}
h1 {
  font-size: 23.94px;
  font-weight: 600;
}
h2 {
  font-size: 23.94px;
  font-weight: 300;
}
h3 {
  font-size: 20.02px;
  font-weight: 300;
}
h4 {
  font-size: 18.06px;
}
h5 {
  font-size: 14px;
}
h6 {
  font-size: 12.04px;
}
h1,
h2,
h3 {
  margin-top: 21px;
  margin-bottom: 10.5px;
}
h1 small,
h2 small,
h3 small,
h1 .small,
h2 .small,
h3 .small {
  font-size: 65%;
}
h4,
h5,
h6 {
  margin-top: 10.5px;
  margin-bottom: 10.5px;
}
h4 small,
h5 small,
h6 small,
h4 .small,
h5 .small,
h6 .small {
  font-size: 75%;
}
a {
  color: #419bf9;
}
a:hover {
  color: #3187e1;
  cursor: pointer;
}
a:active {
  color: #3187e1;
}
a:visisted {
  color: #3187e1;
}
p {
  margin: 0 0 10.5px;
}
.lead {
  margin-bottom: 21px;
  font-size: 16px;
  font-weight: 300;
  line-height: 1.4;
}
small,
.small {
  font-size: 93%;
}
mark,
.mark {
  background-color: #fcf8e3;
  padding: .2em;
}
.text-left {
  text-align: left;
}
.text-right {
  text-align: right;
}
.text-center {
  text-align: center;
}
.text-justify {
  text-align: justify;
}
.text-nowrap {
  white-space: nowrap;
}
.text-lowercase {
  text-transform: lowercase;
}
.text-uppercase {
  text-transform: uppercase;
}
.text-capitalize {
  text-transform: capitalize;
}
.text-muted {
  color: #9da4aa;
}
.text-primary {
  color: #87939a;
}
a.text-primary:hover {
  color: #6d7a81;
}
.text-success {
  color: #3c763d;
}
a.text-success:hover {
  color: #2b542c;
}
.text-info {
  color: #31708f;
}
a.text-info:hover {
  color: #245269;
}
.text-warning {
  color: #8a6d3b;
}
a.text-warning:hover {
  color: #66512c;
}
.text-danger {
  color: #a94442;
}
a.text-danger:hover {
  color: #843534;
}
.bg-primary {
  color: #fff;
  background-color: #87939a;
}
a.bg-primary:hover {
  background-color: #6d7a81;
}
.bg-success {
  background-color: #dff0d8;
}
a.bg-success:hover {
  background-color: #c1e2b3;
}
.bg-info {
  background-color: #d9edf7;
}
a.bg-info:hover {
  background-color: #afd9ee;
}
.bg-warning {
  background-color: #fcf8e3;
}
a.bg-warning:hover {
  background-color: #f7ecb5;
}
.bg-danger {
  background-color: #f2dede;
}
a.bg-danger:hover {
  background-color: #e4b9b9;
}
ul,
ol {
  margin-top: 0;
  margin-bottom: 10.5px;
}
ul ul,
ol ul,
ul ol,
ol ol {
  margin-bottom: 0;
}
.list-unstyled {
  padding-left: 0;
  list-style: none;
}
.list-inline {
  padding-left: 0;
  list-style: none;
  margin-left: -5px;
}
.list-inline > li {
  display: inline-block;
  padding-left: 5px;
  padding-right: 5px;
}
dl {
  margin-top: 0;
  margin-bottom: 21px;
}
dt,
dd {
  line-height: 1.5;
}
dt {
  font-weight: bold;
}
dd {
  margin-left: 0;
}
abbr[title],
abbr[data-original-title] {
  cursor: help;
  border-bottom: 1px dotted #77828d;
}
.initialism {
  font-size: 90%;
  text-transform: uppercase;
}
blockquote {
  padding: 10.5px 21px;
  margin: 0 0 21px;
  border-left: 5px solid #f6f7f8;
}
blockquote p:last-child,
blockquote ul:last-child,
blockquote ol:last-child {
  margin-bottom: 0;
}
blockquote footer,
blockquote small,
blockquote .small {
  display: block;
  font-size: 80%;
  line-height: 1.5;
  color: #77828d;
}
blockquote footer:before,
blockquote small:before,
blockquote .small:before {
  content: '\2014 \00A0';
}
.blockquote-reverse,
blockquote.pull-right {
  padding-right: 15px;
  padding-left: 0;
  border-right: 5px solid #f6f7f8;
  border-left: 0;
  text-align: right;
}
.blockquote-reverse footer:before,
blockquote.pull-right footer:before,
.blockquote-reverse small:before,
blockquote.pull-right small:before,
.blockquote-reverse .small:before,
blockquote.pull-right .small:before {
  content: '';
}
.blockquote-reverse footer:after,
blockquote.pull-right footer:after,
.blockquote-reverse small:after,
blockquote.pull-right small:after,
.blockquote-reverse .small:after,
blockquote.pull-right .small:after {
  content: '\00A0 \2014';
}
address {
  margin-bottom: 21px;
  font-style: normal;
  line-height: 1.5;
}
input[type="text"],
input[type="email"],
input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="month"],
input[type="number"],
input[type="password"],
input[type="range"],
input[type="search"],
input[type="tel"],
input[type="time"],
input[type="url"] {
  width: 100%;
  padding-left: 5px;
  padding-right: 5px;
  line-height: 21px;
  font-weight: 400;
  background: #ffffff;
  color: #697278;
  border-radius: 3px;
  border: 1px solid rgba(230, 230, 230, 0.9);
}
input[type="text"]:disabled,
input[type="email"]:disabled,
input[type="date"]:disabled,
input[type="datetime"]:disabled,
input[type="datetime-local"]:disabled,
input[type="month"]:disabled,
input[type="number"]:disabled,
input[type="password"]:disabled,
input[type="range"]:disabled,
input[type="search"]:disabled,
input[type="tel"]:disabled,
input[type="time"]:disabled,
input[type="url"]:disabled {
  background: rgba(255, 255, 255, 0.6);
}
textarea:focus,
input[type="text"]:focus,
input[type="email"]:focus,
.search-bar .menu .header-container input:focus {
  border: 1px solid #87939a;
  box-shadow: 0 0 1.5px #87939a;
}
body.platform-win32 input[type="text"],
body.platform-win32 input[type="email"],
body.platform-win32 input[type="date"],
body.platform-win32 input[type="datetime"],
body.platform-win32 input[type="datetime-local"],
body.platform-win32 input[type="month"],
body.platform-win32 input[type="number"],
body.platform-win32 input[type="password"],
body.platform-win32 input[type="range"],
body.platform-win32 input[type="search"],
body.platform-win32 input[type="tel"],
body.platform-win32 input[type="time"],
body.platform-win32 input[type="url"] {
  border: 0;
  border-radius: 0;
  box-shadow: 0 0 0 2px rgba(230, 230, 230, 0.9);
}
body.platform-win32 input[type="text"]:focus,
body.platform-win32 input[type="email"]:focus,
body.platform-win32 input[type="date"]:focus,
body.platform-win32 input[type="datetime"]:focus,
body.platform-win32 input[type="datetime-local"]:focus,
body.platform-win32 input[type="month"]:focus,
body.platform-win32 input[type="number"]:focus,
body.platform-win32 input[type="password"]:focus,
body.platform-win32 input[type="range"]:focus,
body.platform-win32 input[type="search"]:focus,
body.platform-win32 input[type="tel"]:focus,
body.platform-win32 input[type="time"]:focus,
body.platform-win32 input[type="url"]:focus {
  border: 0;
  border-radius: 0;
  box-shadow: 0 0 0 2px rgba(179, 179, 179, 0.9);
}
button,
html input[type="button"] {
  cursor: default;
}
button:hover,
html input[type="button"]:hover,
button:active,
html input[type="button"]:active {
  cursor: default;
}
body.platform-win32 .btn {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
  border: 0;
  box-shadow: 0 0 0 2px rgba(230, 230, 230, 0.8);
}
body.platform-win32 .btn:hover {
  background: rgba(230, 230, 230, 0.8);
}
body.platform-win32 .btn.btn-action {
  border: 2px solid #498e38;
}
body.platform-win32 .btn.btn-action:hover {
  background: #5cb346;
}
body.platform-win32 .btn.btn-emphasis {
  background: #5b90fb;
  border: 0;
}
body.platform-win32 .btn.btn-emphasis:hover {
  border-radius: 0;
  background: #296efa;
}
body.platform-win32 .btn.btn-emphasis:active {
  background: #5b90fb;
  box-shadow: 0 0 0;
}
body.platform-win32 .btn.btn-danger,
body.platform-win32 .btn .btn-destructive {
  background: #d9534f;
  border: 0;
}
body.platform-win32 .btn.btn-danger:hover,
body.platform-win32 .btn .btn-destructive:hover {
  border-radius: 0;
  background: #c9302c;
}
.btn {
  padding: 0 0.8em;
  border-radius: 3px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #87939a;
  background: #ffffff;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 0.5px 1px rgba(0, 0, 0, 0.15);
  height: 1.9em;
  line-height: 1.9em;
  font-size: 13.02px;
}
.btn img.content-mask {
  background-color: #87939a;
}
.btn .text {
  margin-left: 6px;
}
.btn:active {
  cursor: default;
  background: #e8e8e8;
}
.btn:focus {
  outline: none;
}
.btn.btn-small {
  font-size: 12.04px;
}
.btn.btn-large {
  font-size: 14px;
  padding: 0 1.3em;
  line-height: 2.2em;
  height: 2.3em;
}
.btn.btn-larger {
  font-size: 15.96px;
  padding: 0 1.6em;
}
.btn.btn-action {
  color: #697278;
  background: #5cb346;
}
.btn.btn-action img.content-mask {
  background-color: #697278;
}
.btn.btn-disabled {
  color: rgba(135, 147, 154, 0.6);
  background: rgba(255, 255, 255, 0.85);
}
.btn.btn-disabled:active {
  background: rgba(255, 255, 255, 0.85);
}
.btn.btn-emphasis {
  position: relative;
  color: #87939a;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #87939a;
}
.btn.btn-emphasis.btn-disabled {
  opacity: 0.4;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 4px;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
}
.btn.btn-danger,
.btn .btn-destructive {
  color: #87939a;
  background: #d9534f;
}
.btn.btn-danger img.content-mask,
.btn .btn-destructive img.content-mask {
  background-color: #87939a;
}
.btn-toolbar {
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f3f3f3));
  padding: 0 13px;
}
.btn-toolbar.narrow {
  padding: 0 9px;
}
.btn-gradient {
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f3f3f3));
}
.btn-gradient:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#e8e8e8), to(#dddddd));
}
.btn-icon {
  background: transparent;
  border: 0;
  box-shadow: none;
  color: #9da4aa;
  margin-right: 10px;
  outline: none !important;
  font-size: 20px;
}
.btn-icon img.content-mask {
  background-color: #9da4aa;
}
.btn-icon:last-child {
  margin-right: 0;
}
.btn-icon.inverse {
  color: #87939a;
}
.btn-icon.inverse img.content-mask {
  background-color: #87939a;
}
.btn-icon.inverse:hover {
  color: white;
}
.btn-icon.inverse:active {
  color: #87939a;
}
.btn-icon.inverse:active img.content-mask {
  background-color: #87939a;
}
.btn-icon:hover {
  cursor: default;
  color: #419bf9;
  box-shadow: none;
}
.btn-icon:hover img.content-mask {
  background-color: #419bf9;
}
.btn-icon:active {
  color: #3187e1;
  box-shadow: none;
}
.btn-icon:active img.content-mask {
  background-color: #3187e1;
}
select {
  border: 1px solid rgba(230, 230, 230, 0.9);
  color: #697278;
  background: #ffffff;
}
html,
body {
  width: 100%;
  height: 100%;
  overflow: hidden;
  font-family: "Nylas-Pro", "Helvetica", sans-serif;
  font-size: 14px;
  line-height: 1.5;
  -webkit-font-smoothing: antialiased;
}
nylas-workspace {
  display: block;
  height: 100%;
  overflow: hidden;
  position: relative;
  font-family: "Nylas-Pro", "Helvetica", sans-serif;
  background-color: #ffffff;
}
nylas-workspace nylas-workspace-axis.horizontal {
  display: -webkit-flex;
  height: 100%;
}
nylas-workspace nylas-workspace-axis.vertical {
  display: -webkit-flex;
  -webkit-flex: 1;
  -webkit-flex-flow: column;
}
.sheet-container {
  height: 100%;
}
.sheet {
  background-color: #ffffff;
}
.sheet-stack-enter {
  left: 30px;
  opacity: 0;
  transition: all 0.125s ease-out;
}
.sheet-stack-enter.sheet-stack-enter-active {
  left: 0;
  opacity: 1;
}
.sheet-stack-leave {
  left: 0;
  opacity: 1;
  transition: all 0.125s ease-in;
}
.sheet-stack-leave.sheet-stack-leave-active {
  left: 30px;
  opacity: 0;
}
.toolbar-menu-control {
  display: none;
}
.toolbar-window-controls {
  margin-top: 9px;
  margin-left: 7px;
  order: -1000;
  min-width: 72px;
  width: 72px;
  flex-grow: 0;
  flex-shrink: 0;
}
.toolbar-window-controls:hover button {
  background-position: 0 -12px;
}
.toolbar-window-controls button {
  -webkit-app-region: no-drag;
  display: inline-block;
  padding: 0;
  width: 12px;
  height: 12px;
  margin: 4px;
  float: left;
  background-color: transparent;
  background-repeat: no-repeat;
  background-position: 0 0;
  background-size: 12px 48px;
  border: 0;
}
.toolbar-window-controls button:active {
  background-position: 0 -24px;
}
.toolbar-window-controls .close {
  background-image: url("images/application-frame/close@1x.png");
}
.toolbar-window-controls .minimize {
  background-image: url("images/application-frame/minimize@1x.png");
}
.toolbar-window-controls .maximize {
  background-image: url("images/application-frame/fullscreen@1x.png");
}
.toolbar-window-controls.alt-true .maximize {
  background-image: url("images/application-frame/maximize@1x.png");
}
@media (-webkit-min-device-pixel-ratio: 2) {
  .toolbar-window-controls .close {
    background-image: url("images/application-frame/close@2x.png");
  }
  .toolbar-window-controls .minimize {
    background-image: url("images/application-frame/minimize@2x.png");
  }
  .toolbar-window-controls .maximize {
    background-image: url("images/application-frame/fullscreen@2x.png");
  }
  .toolbar-window-controls.alt-true .maximize {
    background-image: url("images/application-frame/maximize@2x.png");
  }
}
body.is-blurred .toolbar-window-controls button {
  background-position: 0 -36px;
}
body.is-blurred .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #ffffff, #ffffff);
}
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar {
  background: none;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar img {
  opacity: 0.5;
}
body.is-blurred .sheet-toolbar-container .item-container .window-title {
  opacity: 0.5;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(#ffffff 0%, #ffffff 1.9%, #ffffff 2%, #ffffff 100%);
  color: #434648;
}
.layout-mode-popout .sheet-toolbar {
  background: #ffffff;
  height: 35px;
  min-height: 35px;
  max-height: 35px;
}
.layout-mode-popout .toolbar-window-controls {
  margin-top: 7px;
}
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #e8e8e8;
  width: 100%;
  height: 38px;
  min-height: 38px;
  max-height: 38px;
