.scroll-tooltip {
  background: -webkit-gradient(linear, left top, left bottom, from(rgba(99, 102, 103, 0.9)), to(rgba(82, 85, 86, 0.9)));
  color: white;
  box-shadow: 0 2px 7px rgba(0, 0, 0, 0.25);
  padding: 5px 12px 5px 12px;
  border: 1px solid rgba(54, 56, 57, 0.9);
  border-radius: 3px;
  transform: translate(-15px, 0);
  position: relative;
  white-space: nowrap;
  pointer-events: none;
}
.scroll-tooltip:after,
.scroll-tooltip:before {
  left: 100%;
  top: 50%;
  border: solid transparent;
  content: " ";
  height: 0;
  width: 0;
  position: absolute;
  pointer-events: none;
}
.scroll-tooltip:after {
  border-color: transparent;
  border-left-color: #6a6e6f;
  border-width: 8px;
  margin-top: -8px;
}
.scroll-tooltip:before {
  border-color: transparent;
  border-left-color: rgba(4, 5, 5, 0.9);
  border-width: 9px;
  margin-top: -9px;
}
.scroll-region {
  position: relative;
}
.scroll-region ::-webkit-scrollbar {
  display: none;
}
.scroll-region .scroll-region-content {
  position: absolute;
  height: 100%;
  width: 100%;
  overflow-y: scroll;
  overflow-x: hidden;
  z-index: 1;
}
.scroll-region .scroll-region-content-inner {
  transform: translate3d(0, 0, 0);
}
.scroll-region.scrolling .scroll-region-content-inner {
  pointer-events: none;
}
.scrollbar-track {
  position: relative;
  opacity: 0;
  transition: opacity 0.3s;
  transition-delay: 0.5s;
  padding: 3px;
  width: 17px;
  background: #ffffff;
  border-left: 1px solid #e6e6e6;
  /* Used to read the track height with padding applied. */
}
.scrollbar-track:hover {
  opacity: 1;
  transition-delay: 0s;
}
.scrollbar-track.scrolling {
  opacity: 1;
  transition-delay: 0s;
}
.scrollbar-track.with-ticks {
  opacity: 1;
  transition-delay: 0s;
}
.scrollbar-track.dragging {
  opacity: 1;
}
.scrollbar-track.dragging .scrollbar-handle {
  cursor: default;
  background-color: #a5adb4;
  border: 1px solid #8a939d;
}
.scrollbar-track.dragging .scrollbar-handle .tooltip {
  opacity: 1;
  display: block;
}
.scrollbar-track .scrollbar-track-inner {
  height: 100%;
}
.scrollbar-track .scrollbar-handle {
  background-color: #c1c6cc;
  border: 1px solid #a5adb4;
  border-radius: 8px;
}
.scrollbar-track .scrollbar-handle .tooltip {
  opacity: 0;
  display: none;
  transition: opacity 0.3s;
  top: 50%;
  transform: translate(-100%, -50%);
  position: absolute;
  pointer-events: none;
}
.scrollbar-track .scrollbar-ticks {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 2;
}
.scrollbar-track .scrollbar-ticks .t {
  position: absolute;
  width: 90%;
  left: 5%;
  height: 2px;
  background: #fff000;
  box-shadow: 0 0.5px 0.5px rgba(0, 0, 0, 0.25);
}
.scrollbar-track .scrollbar-ticks .t.match {
  background: #ff8b1a;
  z-index: 2;
}
