.empty-state {
  position: absolute;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
  overflow: hidden;
}
.empty-state > div {
  opacity: 0;
  transition: opacity 1s ease-in;
  width: 100%;
  height: 100%;
}
.empty-state .perspective-empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
