css:
/**
 * Colors
 */
/**
 * Borders
 */
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(230, 230, 230, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(230, 230, 230, 0.8);
}
button,
html input[type="button"] {
  cursor: default;
}
button:hover,
html input[type="button"]:hover,
button:active,
html input[type="button"]:active {
  cursor: default;
}
body.platform-win32 .btn {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
  border: 0;
  box-shadow: 0 0 0 2px rgba(230, 230, 230, 0.8);
}
body.platform-win32 .btn:hover {
  background: rgba(230, 230, 230, 0.8);
}
body.platform-win32 .btn.btn-action {
  border: 2px solid #498e38;
}
body.platform-win32 .btn.btn-action:hover {
  background: #5cb346;
}
body.platform-win32 .btn.btn-emphasis {
  background: #5b90fb;
  border: 0;
}
body.platform-win32 .btn.btn-emphasis:hover {
  border-radius: 0;
  background: #296efa;
}
body.platform-win32 .btn.btn-emphasis:active {
  background: #5b90fb;
  box-shadow: 0 0 0;
}
body.platform-win32 .btn.btn-danger,
body.platform-win32 .btn .btn-destructive {
  background: #d9534f;
  border: 0;
}
body.platform-win32 .btn.btn-danger:hover,
body.platform-win32 .btn .btn-destructive:hover {
  border-radius: 0;
  background: #c9302c;
}
.btn {
  padding: 0 0.8em;
  border-radius: 3px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #87939a;
  background: #ffffff;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 0.5px 1px rgba(0, 0, 0, 0.15);
  height: 1.9em;
  line-height: 1.9em;
  font-size: 13.02px;
}
.btn img.content-mask {
  background-color: #87939a;
}
.btn .text {
  margin-left: 6px;
}
.btn:active {
  cursor: default;
  background: #e8e8e8;
}
.btn:focus {
  outline: none;
}
.btn.btn-small {
  font-size: 12.04px;
}
.btn.btn-large {
  font-size: 14px;
  padding: 0 1.3em;
  line-height: 2.2em;
  height: 2.3em;
}
.btn.btn-larger {
  font-size: 15.96px;
  padding: 0 1.6em;
}
.btn.btn-action {
  color: #697278;
  background: #5cb346;
}
.btn.btn-action img.content-mask {
  background-color: #697278;
}
.btn.btn-disabled {
  color: rgba(135, 147, 154, 0.6);
  background: rgba(255, 255, 255, 0.85);
}
.btn.btn-disabled:active {
  background: rgba(255, 255, 255, 0.85);
}
.btn.btn-emphasis {
  position: relative;
  color: #87939a;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #87939a;
}
.btn.btn-emphasis.btn-disabled {
  opacity: 0.4;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 4px;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
}
.btn.btn-danger,
.btn .btn-destructive {
  color: #87939a;
  background: #d9534f;
}
.btn.btn-danger img.content-mask,
.btn .btn-destructive img.content-mask {
  background-color: #87939a;
}
.btn-toolbar {
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f3f3f3));
  padding: 0 13px;
}
.btn-toolbar.narrow {
  padding: 0 9px;
}
.btn-gradient {
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f3f3f3));
}
.btn-gradient:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#e8e8e8), to(#dddddd));
}
.btn-icon {
  background: transparent;
  border: 0;
  box-shadow: none;
  color: #9da4aa;
  margin-right: 10px;
  outline: none !important;
  font-size: 20px;
}
.btn-icon img.content-mask {
  background-color: #9da4aa;
}
.btn-icon:last-child {
  margin-right: 0;
}
.btn-icon.inverse {
  color: #87939a;
}
.btn-icon.inverse img.content-mask {
  background-color: #87939a;
}
.btn-icon.inverse:hover {
  color: white;
}
.btn-icon.inverse:active {
  color: #87939a;
}
.btn-icon.inverse:active img.content-mask {
  background-color: #87939a;
}
.btn-icon:hover {
  cursor: default;
  color: #419bf9;
  box-shadow: none;
}
.btn-icon:hover img.content-mask {
  background-color: #419bf9;
}
.btn-icon:active {
  color: #3187e1;
  box-shadow: none;
}
.btn-icon:active img.content-mask {
  background-color: #3187e1;
}
body.platform-win32 .composer-inner-wrap .composer-drop-cover {
  border-radius: 0;
}
body.platform-win32 .composer-inner-wrap .composer-action-bar-wrap {
  border-radius: 0;
}
body.platform-win32 .composer-inner-wrap input,
body.platform-win32 .composer-inner-wrap input:focus {
  box-shadow: none;
}
.composer-inner-wrap {
  position: relative;
  height: 100%;
  display: flex;
  flex-direction: column;
}
.composer-inner-wrap .composer-drop-cover {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1000;
  background: rgba(255, 255, 255, 0.7);
  border-radius: 3px;
  border: 4px dashed #a5adb4;
  text-align: center;
  line-height: 2.3em;
  pointer-events: none;
}
.composer-inner-wrap .composer-drop-cover .centered {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  color: #8a939d;
  font-weight: 500;
  font-size: 1.1em;
}
.composer-inner-wrap .composer-drop-cover .centered img {
  margin: auto;
  display: block;
  margin-bottom: 20px;
  background-color: #8a939d;
}
.composer-inner-wrap .inline-image-upload-container {
  display: inline-block;
}
.composer-inner-wrap .inline-image-upload-container .file-wrap.file-image-wrap {
  margin: 0;
  max-width: 100%;
  min-width: 0;
}
.composer-inner-wrap .composer-action-bar-wrap {
  position: relative;
  width: 100%;
  background: #fdfdfd;
  border-top: 1px solid #ebebeb;
  box-shadow: inset 0 2px 1px rgba(0, 0, 0, 0.03);
  border-bottom: 0;
  border-radius: 3px;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-cover {
  background-image: -webkit-linear-gradient(left, rgba(253, 253, 253, 0) 0%, #fdfdfd 10%);
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis) {
  background: transparent;
  box-shadow: 0 0 0;
  margin: 0;
  padding: 0 9px;
  white-space: nowrap;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis) img.content-mask {
  background-color: rgba(129, 141, 147, 0.7);
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis):hover img.content-mask {
  background-color: rgba(129, 141, 147, 0.95);
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis).btn-enabled {
  color: #566065;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis).btn-enabled img.content-mask {
  background-color: #566065;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send {
  margin-right: 10px;
  white-space: nowrap;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content {
  display: flex;
  margin: 0 auto;
  flex-direction: row;
  max-width: 800px;
  padding: 9px 22.5px;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content .composer-action-bar-plugins {
  flex-wrap: wrap;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap {
  position: relative;
  overflow: hidden;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap .composer-action-bar-plugins {
  opacity: 0;
  transition: opacity 30ms;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap .action-bar-cover {
  transition: left 200ms ease-out;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap.plugins-loaded .composer-action-bar-plugins {
  opacity: 1;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap.plugins-loaded .action-bar-cover {
  left: 100%;
}
.composer-inner-wrap .composer-content-wrap {
  padding: 0;
  flex: 1;
  display: flex;
  position: relative;
  flex-flow: column;
}
.composer-inner-wrap .composer-centered {
  display: flex;
  position: relative;
  flex-direction: column;
  flex: 1;
  width: 100%;
  max-width: 800px;
  margin: 0 auto;
  padding-top: 9.8px;
}
.composer-inner-wrap .text-actions {
  text-align: right;
  line-height: 1.4;
  position: relative;
  top: -3px;
}
.composer-inner-wrap .composer-body-wrap {
  padding: 20px 14px 5px 14px;
}
.composer-inner-wrap .composer-header-actions {
  position: relative;
  float: right;
  z-index: 2;
  cursor: default;
  padding-right: 21px;
  padding-top: 12px;
}
.composer-inner-wrap .composer-header-actions .action {
  color: #87939a;
  font-size: 13.02px;
  padding: 10px 6px;
}
.composer-inner-wrap .composer-header-actions .action img.content-mask {
  background-color: #87939a;
}
.composer-inner-wrap .composer-header-actions .action:first-child {
  padding-left: 14px;
}
.composer-inner-wrap .composer-header-actions .action:last-child {
  padding-right: 0;
}
.composer-inner-wrap .composer-header-actions .action:hover {
  color: #419bf9;
  cursor: default;
}
.composer-inner-wrap .composer-header-actions .action:hover img.content-mask {
  background-color: #419bf9;
}
.composer-inner-wrap input,
.composer-inner-wrap textarea {
  color: #697278;
  position: relative;
  display: block;
  background: inherit;
  width: 100%;
  resize: none;
  border: none;
}
.composer-inner-wrap .composer-field-label {
  color: #87939a;
  float: left;
  padding-top: 13px;
  display: block;
}
.composer-inner-wrap .collapsed-composer-participants {
  position: relative;
  margin: 0 23px;
  border-bottom: 1px solid #e6e6e6;
  flex-shrink: 0;
  color: #87939a;
  padding: 10px 0 10px 0;
}
.composer-inner-wrap .collapsed-composer-participants .collapsed-contact {
  padding-right: 0.25em;
  color: #697278;
}
.composer-inner-wrap .collapsed-composer-participants .collapsed-contact:after {
  content: ",";
}
.composer-inner-wrap .collapsed-composer-participants .collapsed-contact:last-child:after {
  content: "";
}
.composer-inner-wrap .collapsed-composer-participants .num-remaining.token {
  color: #697278;
  padding-right: 12px;
  margin-left: 0;
  margin-top: 2px;
}
.composer-inner-wrap .collapsed-composer-participants .num-remaining-wrap {
  position: absolute;
  right: -3px;
  z-index: 2;
  top: 6px;
}
.composer-inner-wrap .collapsed-composer-participants .num-remaining-wrap .show-more-fade {
  position: absolute;
  width: 190px;
  height: 32px;
  right: 0;
  top: 0;
  background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, #ffffff 40%);
}
.composer-inner-wrap .composer-subject {
  position: relative;
  margin: 0 23px;
  border-bottom: 1px solid #e6e6e6;
  flex-shrink: 0;
}
.composer-inner-wrap .composer-subject input {
  display: inline-block;
  padding: 13px 0 9px 0;
  min-width: 5em;
  background-color: transparent;
  border: none;
  margin: 0;
}
.composer-inner-wrap .composer-subject input::-webkit-input-placeholder {
  color: #87939a;
}
.composer-inner-wrap .composer-subject input:focus {
  box-shadow: none;
}
.composer-inner-wrap .compose-body-scroll {
  position: initial;
}
.composer-inner-wrap .compose-body-scroll .scroll-region-content .scroll-region-content-inner {
  min-height: 100%;
  display: flex;
}
.composer-inner-wrap .compose-body {
  flex: 1;
  cursor: text;
  overflow-x: auto;
  position: relative;
  margin: 0 8px;
}
.composer-inner-wrap .compose-body .quoted-text-control {
  position: relative;
  margin: 0 14px 14px 0;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text {
  display: none;
  cursor: pointer;
  position: absolute;
  z-index: 2;
  right: -6px;
  top: -6px;
  border-radius: 0 0 0 3px;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text:active {
  background: none;
  -webkit-filter: brightness(95%);
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text img {
  height: 24px;
}
.composer-inner-wrap .compose-body .quoted-text-control:hover .remove-quoted-text {
  display: block;
}
.composer-inner-wrap .compose-body div[contenteditable] {
  min-height: 70px;
}
.composer-inner-wrap .composer-footer-region {
  cursor: default;
}
.composer-inner-wrap .composer-footer-region:hover {
  cursor: default;
}
.composer-inner-wrap .attachments-area {
  padding: 0;
  margin: 0;
}
.composer-full-window {
  width: 100%;
  height: 100%;
}
.composer-full-window .composer-outer-wrap {
  width: 100%;
  height: 100%;
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap {
  background: #fcfcfc;
  border-top: 1px solid #ebebeb;
  box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.03);
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap .action-bar-cover {
  background-image: -webkit-linear-gradient(left, rgba(252, 252, 252, 0) 0%, #fcfcfc 10%);
}
.composer-full-window .composer-inner-wrap .composer-action-bar-content {
  padding: 9px 13.5px 9px 22.5px;
}
.composer-full-window .composer-inner-wrap .compose-body {
  margin-bottom: 0;
  position: relative;
}
.composer-full-window .compose-body div[contenteditable] {
  min-height: 21px;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #fffcf5;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .btn.btn-toolbar.btn-trash {
  padding-right: 0;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .show-more-fade {
  background: linear-gradient(to right, rgba(255, 252, 245, 0) 0%, #fffcf5 40%);
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .composer-action-bar-wrap {
  background: #fdfaf3;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .composer-action-bar-wrap .action-bar-cover {
  background-image: -webkit-linear-gradient(left, rgba(253, 250, 243, 0) 0%, #fdfaf3 10%);
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #87939a;
  background-color: #ffffff;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .show-more-fade {
  background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, #ffffff 40%);
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .composer-action-bar-wrap {
  background: #fdfdfd;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .composer-action-bar-wrap .action-bar-cover {
  background-image: -webkit-linear-gradient(left, rgba(253, 253, 253, 0) 0%, #fdfdfd 10%);
}
.composer-participant-field {
  position: relative;
  margin: 0 22px;
  flex-shrink: 0;
  border-bottom: 1px solid #e6e6e6;
  min-height: 46px;
  color: #697278;
}
.composer-participant-field .button-dropdown {
  margin-left: 10px;
  padding-top: 11px;
  vertical-align: -webkit-baseline-middle;
}
.composer-participant-field .button-dropdown .primary-item,
.composer-participant-field .button-dropdown .only-item {
  line-height: 2em;
}
.composer-participant-field .button-dropdown:hover .primary-item,
.composer-participant-field .button-dropdown:hover .only-item {
  border-radius: 3px;
}
.composer-participant-field .button-dropdown .secondary-items {
  border-radius: 3px;
}
.composer-participant-field .button-dropdown .item .contact.is-alias {
  font-style: italic;
  padding-left: 10px;
  border-left: 2px solid #e6e6e6;
  margin-left: 2px;
}
.composer-participant-field .participant {
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}
.composer-participant-field .participant .participant-primary {
  font-weight: 600;
}
.composer-participant-field .participant .participant-secondary {
  color: #87939a;
}
.overlaid-components {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 0;
  line-height: 1.4;
  overflow: visible;
  z-index: 1;
}
img.n1-overlaid-component-anchor-container {
  border: 0;
  vertical-align: baseline;
}
.toggle-preview {
  position: absolute;
  top: -17px;
  right: 0;
  font-size: 12.04px;
  opacity: 0.3;
  cusor: pointer;
}
.toggle-preview:hover {
  cusor: pointer;
  opacity: 1;
}
